INSERT INTO `article` (`articleid`, `sortid`, `title`, `source`, `author`, `userid`, `content`, `click`, `publish_time`, `launch_time`, `imageid`, `keyword1`, `keyword2`, `keyword3`) VALUES
(156, 11, '转发电子科技大学－美国韦伯斯特大学合作国际工商管理硕士(IMBA)招生简章', '', NULL, 0, '<P>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <FONT size=3><STRONG>IMBA招生简章(2009,第七届) </STRONG></FONT></P>\r\n<P><FONT size=3><STRONG>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;中国电子科技大学－美国韦伯斯特大学合作国际工商管理硕士(IMBA)项目</STRONG></FONT></P>\r\n<P><FONT size=3><STRONG>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （咨询电话：83200870/经济与管理学院）</STRONG></FONT>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </P>\r\n<P>&nbsp;</P>\r\n<P>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <FONT size=3>中国电子科技大学―美国韦伯斯特大学合作国际工商管理硕士（IMBA）是经中华人民共和国教育部《教外综函（2002）47号》文和国务院学位办《学位办（2002） 90号、（2003）141号》文正式批准的中外合作办学项目（项目批准书编号MOE51US1A20020160O）。电子科技大学是全国重点大学，经济与管理学院师资力量雄厚，办学经验丰富。而开展国际办学，培养工商管理硕士，则是韦伯斯特大学的重要特色。美国韦伯斯特大学校本部设在美国密苏里州的圣.路易斯市，并在英国、荷兰、瑞士、奥地利、泰国、中国等国都有分校及合作项目。韦伯斯特大学商学院是获得美国大学商学教育权威认证机构――美国大学商学院联盟(ACBSP)认证的商学院，中美两校联合办学，强强联合、优势互补，将有力地促进我国的工商管理硕士教育的发展。从2003年开始，IMBA项目已经成功招收了六届学生，其中五届已经顺利毕业。 </FONT></P>\r\n<P><FONT size=3></FONT>&nbsp;</P>\r\n<P><FONT size=3><STRONG><FONT size=4>项目特色</FONT></STRONG> </FONT></P>\r\n<P><FONT size=3>★中国西南片区首家与国外合作IMBA项目；</FONT></P>\r\n<P><FONT size=3>★不通过国家联考，学分制，无学位论文答辩； </FONT></P>\r\n<P><FONT size=3>★学位课程全英文教学，外国教授面对面授课；</FONT></P>\r\n<P><FONT size=3>★授予国家承认的美国MBA学位； </FONT></P>\r\n<P><FONT size=3>★不出国门即可实现您的留学梦想！ </FONT></P>\r\n<P><FONT size=3><STRONG><FONT size=4>我们的目标：</FONT></STRONG> </FONT></P>\r\n<P><FONT size=3>培养具备在全球化、知识化、信息化的现代商业环境中从事跨国经营管理能力的国际化工商管理人才！ </FONT></P>\r\n<P><FONT size=3></FONT>&nbsp;</P>\r\n<P><FONT size=3><STRONG>一、招生与录取</STRONG>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </FONT></P>\r\n<P><FONT size=3>1，&nbsp; 报名条件：具有学士学位或大学本科毕业学历。</FONT></P>\r\n<P><FONT size=3>2,&nbsp;&nbsp; 招生名额：2009年(第七届)计划招收60名。</FONT></P>\r\n<P><FONT size=3>3，&nbsp; 录取程序：报名→提交入学申请表→资格审查→入学英语考试→录取。</FONT></P>\r\n<P><FONT size=3><STRONG>二、 学制、学习方式、学位及费用</STRONG></FONT></P>\r\n<P><FONT size=3>1，&nbsp; 学制：二年（每年五个学期，每学期九周）。 </FONT></P>\r\n<P><FONT size=3>2，&nbsp; 学习方式：在职不脱产学习，周末上课，面对面授课。</FONT></P>\r\n<P><FONT size=3>3， 学位：授予美国韦伯斯特大学工商管理硕士（MBA）学位。</FONT></P>\r\n<P><FONT size=3>4， 学费：人民币74,000元，分两次支付；</FONT></P>\r\n<P><FONT size=3>5,&nbsp; 书费：预收3,000元 (分两次支付)。</FONT></P>\r\n<P><FONT size=3>6，报名费：400元人民币。</FONT></P>\r\n<P><FONT size=3>7，英语强化培训：免费。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </FONT></P>\r\n<P><FONT size=3><STRONG>三、&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 上课地点、赴美短期学习，师资及教材</STRONG></FONT></P>\r\n<P><FONT size=3>1，&nbsp; 本项目授课地点在电子科技大学管理学院（电子信息产业大厦）。</FONT></P>\r\n<P><FONT size=3>2，&nbsp; 每年的夏季学期(Summer Term)，学生可以申请自费赴美国韦伯斯特大学总校（St. Louis）学习一个学期（九周），而后返回，继续学习。</FONT></P>\r\n<P><FONT size=3>3，&nbsp; 全部学位课程均选用国外最新原版工商管理教材。</FONT></P>\r\n<P><FONT size=3>4，&nbsp; 预修课由电子科大教师担任，中/英文双语教学。学位课程全部由美国韦伯斯特大学外籍教授担任，全英语、面对面授课。 </FONT></P>\r\n<P><FONT size=3><STRONG>四、&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 课程设置（32学时/每课程，分九周完成）</STRONG></FONT></P>\r\n<P><FONT size=3>1，预修课（4门）：&nbsp;&nbsp;&nbsp; 国际商务，会计学基础，经济学基础，电子商务。</FONT></P>\r\n<P><FONT size=3>2，核心课（9门）：&nbsp;&nbsp;&nbsp; 营销管理，财务管理，组织行为学，高级企业财务，应用商务统计，管理会计，运作与项目管理，管理经济学，商务政策与战略管理。</FONT></P>\r\n<P><FONT size=3>3，&nbsp; 选修课（统一选3门）： 人力资源管理，市场研究，商务调研方法，国际商法, 投资学，领导学，</FONT><FONT size=3>产品开发，促销与营销沟通，国际谈判技巧，供应链管理，决策支持系统。 </FONT></P>\r\n<P><FONT size=3><STRONG>五、&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 报名申请及入学时间安排 </STRONG></FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp; 2008年11月 ～ 2009年3月&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 报名 </FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp; 2009年3月22日（日）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 入学考试（笔试、口试） </FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp; 2009年3月23日 ～ 3月28日&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 发录取通知，注册，缴费 </FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp; 2009年3月29日&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 新生入学（预修课程开课） </FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp; 2009年6月 ～ 7月&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 英语口语强化培训（每周二次，六周）</FONT></P>\r\n<P><FONT size=3><STRONG>六、报名应提交的申请资料</STRONG> </FONT></P>\r\n<P><FONT size=3>1，&nbsp; 本人学历、学位证件以及大学成绩单的中、英文翻译件各二份(必须由毕业院校加盖公章或公证处出具)； </FONT></P>\r\n<P><FONT size=3>2，&nbsp; 身份证复印件一份； </FONT></P>\r\n<P><FONT size=3>3，&nbsp; 一寸近期正面免冠照片四张，二寸照片二张； </FONT></P>\r\n<P><FONT size=3><STRONG>七、报名地点、时间与咨询电话</STRONG> </FONT></P>\r\n<P><FONT size=3>1，&nbsp; 地址：成都市，建设北路二段4号，邮编610054 </FONT></P>\r\n<P><FONT size=3>电子科技大学经济与管理学院（电子信息产业大厦三楼304室） </FONT></P>\r\n<P><FONT size=3>2，时间：每天上午8:30至下午6:00 </FONT></P>\r\n<P><FONT size=3>3，电话：（028）83200870,&nbsp;&nbsp; </FONT></P>\r\n<P><FONT size=3>4，电子邮件：</FONT><A href="mailto:imba@uestc.edu.cn"><FONT size=3>imba@uestc.edu.cn</FONT></A><FONT size=3> </FONT></P>\r\n<P><FONT size=3>5，网址： </FONT><A href="http://www.mgmt.uestc.edu.cn"><FONT size=3>http://www.mgmt.uestc.edu.cn</FONT></A><FONT size=3>&nbsp; </FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</FONT><A href="http://www.websterchina.com"><FONT size=3>http://www.websterchina.com</FONT></A><FONT size=3> </FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; </FONT><A href="http://www.webster.edu"><FONT size=3>http://www.webster.edu</FONT></A></P>\r\n<P><BR><FONT size=3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;电子科技大学管理学院</FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2009.03.12 </FONT></P>', 1157, 1236864910, '2009-03-12 05:35:10', 0, NULL, NULL, NULL),
(157, 19, '成都蓝雷科技有限公司招聘', '', NULL, 0, '成都蓝雷科技有限公司是由美国电子商务专业服务公司电子商务专家直接领导的，以承接全球电子商务软件定制业务的外包服务公司。<BR>电子邮箱：lanleihr@gmail.com<BR>招聘人数：６人<BR>招聘职位<BR> .Net开发工程师<BR>岗位职责：<BR>完成对软件定制项目的分析、设计和开发任务<BR>岗位要求：<BR>1计算机科学与技术以及相关专业，本科以上学历，<FONT color=#ff0000>英语四级以上</FONT>；<BR>2熟悉Net Framework，掌握<FONT color=#ff0000>C#，</FONT>ASP.Net, XML, XSLT, SQL, T-SQL, ADO.Net,<BR>JavaScript, HTML,Web Service等开发技术；<BR>3有体系结构设计经验和良好的面向对象设计经验；<BR>4能按照规范的软件开发流程,完成软件的编码和测试能力，具有规范的开发文档写作能力；<BR>5 具有电子商务系统开发经验，熟悉网站的架设；<BR>6具有很强的自学能力和英文资料阅读能力；<BR>7 ,为人诚信，爱岗敬业，有责任心，吃苦耐劳，具有团队合作精神和良好的勾通能力。<BR><FONT color=#ff0000>请将个人简历email至 lanleihr@gmail.com 公司将主动与你联系</FONT><BR>', 2590, 1236908870, '2009-03-12 17:47:50', 0, NULL, NULL, NULL),
(158, 11, '美国普渡大学卡尤美分校ETIE访学项目以及2+2双学位项目招生通知', '', NULL, 0, '<P>按照着力推进教育国际化、培养具有国际竞争力人才的学校发展策略，我校与美国普渡大学Calumet（PUC）经过友好协商，签订了在两校间开展ETIE访学项目和2+2本科双学位项目的协议。项目内容具体如下：</P>\r\n<P>一、ETIE访学项目<BR>ETIE项目起始于2006年，由PUC与中国国内几所著名高校联合创建。项目的宗旨是为中国优秀的大学生提供一个在美国英语环境中锻炼提高工程实际应用能力的机会。ETIE项目在学生自愿申请的基础上，从中方项目学校本科大三、大四以及研究生工科专业学生中选拔品学兼优者参加赴美访学。参加访学的大三学生，从第7学期起赴美学习一年；参加访学的大四应届毕业生和研究生可选择春季或秋季赴美学习半年或一年。在美学习期间需根据所学专业完成若干门课程（包括英语课程），课程的具体内容由中方学校与美方学校共同确定。参加访学的大三学生如能够按照PUC的教学要求完成学习任务的学生，可获得中方大学的学分认可，颁发中方大学毕业证和学士学位证书。PUC在给予ETIE项目结业证书基础上，满足以下条件的访学学生，还可以免试GRE获得该校硕士研究生的入学资格（即在ETIE访学项目完成后，学生可以继续在该校攻读硕士学位）：<BR>&nbsp;（1）在PUC学习期间全部课程获得B以上成绩（含B）；<BR>&nbsp;（2）完成中方学校本科教学计划中的全部学习任务，正常毕业；<BR>&nbsp;（3）托福（TOEFL）网考 77分（分项分别高于阅读19、听力14、口语18、写作18）或雅思6.5分以上（托福可以在美方访学期间内考试）。<BR>对普渡大学卡尤美分校的ETIE项目有兴趣的同学可以访问学校网址&nbsp; <A href="http://www.calumet.purdue.edu/engr/etie">www.calumet.purdue.edu/engr/etie</A></P>\r\n<P>二、ETIE访学项目申请条件<BR>1.&nbsp;&nbsp; 参加ETIE访学项目的学生必须是我校全日制大三、大四学生以及研究生，学习成绩优良，GPA达到3.0分以上，英语通过国家四级考试。<BR>2.&nbsp; 专业范围：我校工科各专业。<BR>3.&nbsp; 符合上述条件申请人的基本资料（成绩单及相关材料）将被送达PUC，由PUC最终确定赴美方访学学生名单。</P>\r\n<P>三、2+2本科双学位项目</P>\r\n<P>我校大二学生（理、工、文专业不限）可申请赴PUC续读大三和大四课程，要求申请人在我校的GPA达到3.0以上，托福IBT 79分。凡在电子科技大学和PUC学习期间均顺利完成学业的学生，可分别获得两校的本科学位。</P>\r\n<P>四、赴PUC留学所需费用</P>\r\n<P>在PUC留学每学期所需费用约为12,000美元，主要包括：（1）学费：约6,000美元（PUC的标准为500美元/1学分，最低要求为&nbsp; 每学期12学分）；（2）住宿费、餐费、零用费等。一年全部费用约2万至2.4万美金。 上述费用不包括往返机票、申领护照及办理签证等费用。</P>\r\n<P>五、PUC项目申请办法<BR>1.&nbsp;&nbsp; 在学校国际合作与交流处（主楼中118）申请报名，报名申请时限：每个项目年度的3月19日为申请截止日。<BR>2.&nbsp;&nbsp; 报名申请材料：<BR>1). 下载 电子科技大学海外合作留学申请表，按要求填写，并请所在学院主管教学的副院长签字、盖学院公章。<BR>2).&nbsp;&nbsp; 请点击下载&nbsp;&nbsp;PUC Application Forms, 按要求填写项目申请和住房申请。<BR>3).&nbsp;&nbsp; 英文版的大学成绩清单（校档案馆制作,需计算GPA）。<BR>4).&nbsp;&nbsp; 银行资金证明（等效于2.4万美金的人民币存款证明）。<BR>3.&nbsp;&nbsp; 申请报名是须交300元项目申请办理费</P>\r\n<P>项目咨询：国际合作与交流处&nbsp; 全老师&nbsp;&nbsp;&nbsp; 电话：028-83202357<BR></P>', 856, 1236914070, '2009-03-12 19:14:30', 0, NULL, NULL, NULL),
(159, 11, '美国新墨西哥州立大学2+2双学位留学项目招生通知', '', NULL, 0, '我校与美国新墨西哥州立大学（NMSU）和美国国际经济文化交流协会（WACE）共同合作，在由WACE开创的《1-2-1中美双学位计划》的基础上，签订了三方合作的2+2双学位项目协议。《1-2-1中美双学位计划》已在中美大学间实施了多年，有着良好的声誉，也得到中美政府的一贯支持。 <BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 根据该协议，我校大二学生可申请到新墨西哥州立大学学习大三、大四课程，如顺利完成学业，将分别获得两所大学所颁发的本科学位。该项目下的学生在完成美方大三、大四两年的学习后，可选择继续在美升学，但由于该项目的特点，学生在完成美方大四课程后，需要返回中国重新申请赴美留学签证，方可再赴美学习研究生课程。<BR>中美1-2-1双学位计划项目的优势：<BR>&nbsp;&nbsp;&nbsp;&nbsp;1. 中美大学校际直接交流、WACE全程协调管理提供高质量、中美交替的理想学习生活环境，充分吸取中、美高等教育的各自优势和长处。<BR>&nbsp;&nbsp;&nbsp;&nbsp;2. 1-2-1双学位计划得到美国驻华使领馆备案认可，WACE提供签证辅导，签证率通过率。<BR>&nbsp;&nbsp;&nbsp;&nbsp;3. 1-2-1双学位计划项目的留学申请和签证申请，不再把过关的托福成绩作为必备条件（这一点更加符合中国大学的实际情况，很多学生并无暇顾及托福、雅思的培训和考试或即便考试也因为准备不充分而没有拿到理想的考试成绩）。<BR>&nbsp;&nbsp;&nbsp;&nbsp;4. 中美学分相互承认、学分互换使学生同时获得中美两国大学的两个本科文凭和学士学位证书。<BR>&nbsp;&nbsp;&nbsp;&nbsp;5. 根据毕业学生要求，WACE可协助联系在美国工作实习。<BR>留学新墨西哥州立大学的费用：<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1、学费&nbsp; 13,300美元<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2 、学校食宿费&nbsp;5,100美元<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3&nbsp;、杂费&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 总计约：19,000美元/年<BR>&nbsp;&nbsp;&nbsp;&nbsp;申请去新墨西哥州立大学的同学有可能获得当地学生优惠待遇，即只需要缴纳州内学费，这样每年大约可以节省1万美金，获得州内待遇的必要条件是托福ibt成绩符合其入学要求、学业成绩优秀。<BR>&nbsp;WACE收取的服务管理费： <BR>&nbsp;&nbsp;&nbsp; 一次性收取1-2-1双学位项目的服务管理费每人三千美元（获得签证后才收取）。<BR>&nbsp;项目申请须知：<BR>&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1. 申请人须为：校内一年级、二年级全日制本科生。<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2. 英语听说能力良好、学习成绩良好。<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3. 3月23日前将报名申请资料交到国际合作与交流处。<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4. 申请报名时交项目申请办理费200元。<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5. 申请报名需填写和递交以下资料：<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1）下载 电子科技大学海外合作留学申请表 以及 WACE 1-2-1双学位项&nbsp;目申请表。填写表格，校内的申请表需要请所在学院负责人签字盖章，WACE项目申请表需用英文和汉语拼音填写、贴上近期护照用照片。<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2）大学各学期考试成绩单中英文原件一份（由学校档案馆制作，须计算GPA）。<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3）英语考试成绩单，托福或雅思或CET 四级或六级考试证书（新墨西哥州立大学需要托福、雅思成绩，没有达到标准也不影响入学申请，但在美入学后根据具体情况要求补修或强化）。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 欲了解更多信息可电话咨询国际合作与交流处全老师 83202357 。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 国际合作与交流处<BR>', 632, 1236914163, '2009-03-12 19:16:03', 0, NULL, NULL, NULL),
(160, 12, '关于申报2009年四川省科技进步奖登记的通知', '', NULL, 0, '&nbsp;&nbsp;&nbsp; 2009年四川省科技进步奖申报登记工作将于5月份开始。对于拟申报2009年四川省科技进步奖的项目，请将项目名称于3月19日前报给刘燕老师。', 1039, 1236914881, '2009-03-12 19:28:01', 0, NULL, NULL, NULL),
(161, 12, '数学论坛', '', NULL, 0, '<P>报告题目：Waveform Relaxation Methods of Differential Equations<BR>报 告 人：蒋耀林 教授/博导<BR>时&nbsp;&nbsp;&nbsp; 间：2009年3月26日上午10：30<BR>地&nbsp;&nbsp;&nbsp; 点：主楼东210<BR>报告人介绍：蒋耀林，男，1966年10月生，江苏省扬州人。西安交通大学理学院教授，主要研究方向为微分方程的波形松弛方法，偏微分方程计算的新型算法等，先任中国工业与应用数学学会第四届与第五届常务理事；陕西省工业与应用数学学会第四届理事长。先后出版著作2部，发表各类科技论文130余篇，其中学术期刊论文90余篇（SCI论文近50篇，重要刊物论文30余篇）和大型国际会议论文近30篇。先后主持1项863课题，1项973子课题，4项国家自然科学基金项目，3项教育部项目和1项国家重点实验室项目，获陕西省教委科技壹等奖（1996年），陕西省科技进步贰等奖（1997年），教育部提名国家自然科学贰等奖（2003年）。</P>\r\n<P>报告题目：加速收敛的分裂外推技术<BR>报 告 人：吕涛 教授/博导<BR>时&nbsp;&nbsp;&nbsp; 间：2009.3.26下午3：00<BR>地&nbsp;&nbsp;&nbsp; 点：主楼东210<BR>报告人简介：吕涛，男，1940年11月生，1988年10月-2000年10月任中国科学院成都分院数理科学研究室研究员；2000年10月起任四川大学数学院教授。1992年享受国务院政府津贴，2000年聘为四川省学术和技术带头人。主要从事计算数学和大型科学工程问题算法研究，是最早研究有限元外推法与分裂外推法两大新算法的人之一。 科研成果《有限元外推技术》获中国科学院1989年自然科学一等奖，《气藏数值模拟新算法》获石油天然气总公司（部级）1992年科技进步二等奖，《大型科学计算方法在气藏数值模拟中应用》获1992年四川省科技进步三等奖，《并行解微分积分方程区域分解法与分裂外推法》获教育部2004年自然科学二等奖。发表科研论文百余篇，已出版专著三部：《区域分解算法》，科学出版社，1992；《分裂外推与组合技巧》科学出版社，1998；英文专著《The Splitting Extrapolation Method》，World Scientific Publishing, Singapore, 1995。其中《The Splitting Extrapolation Method》在美国核心期刊SIAM Reviews vol.39(1)上有长篇评述认为：分裂外推法是由中国人所开创的新算法，一切受维数效应困扰者皆可从中受益。</P>\r\n<P>请各位老师及学生积极参加！</P>', 1411, 1236933181, '2009-03-13 00:33:01', 0, NULL, NULL, NULL),
(162, 11, '转发电子科大学生近期（3－4月）留学项目一览', '', NULL, 0, '<P>1.&nbsp;美国普渡大学卡尤美分校“2+2双学位项目”和“ETIE访学项目”<BR>项目简介：“2+2双学位项目”为在本校和对方学校各学习两年，完成毕业要求后，拿双方学校本科学位。“ETIE访学项目”为有期限的访问学习，不拿对方学校学位。<BR>具体申请办法详见：<A href="http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=229">http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=229</A><BR>报名截止日期：2009年3月19日<BR>2.&nbsp;英国格拉斯哥大学联合培养项目<BR>项目简介：包括“2+2”、“4+1”、“3+1”、“3.5+0.5”等培养模式，其中“2+2”和“4+1”模式拿双方学校学位，“3+1”和“3.5+0.5”模式不拿对方学校学位。<BR>具体申请办法详见：<A href="http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=330">http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=330</A><BR>报名截止日期：2009年4月24日<BR>3.&nbsp;美国伊利诺理工大学“2+2双学位项目”<BR>项目简介：在本校和对方学校各学习两年，完成毕业要求后，拿双方学校本科学位。<BR>具体申请办法详见：<A href="http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=154">http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=154</A><BR>报名截止日期：2009年3月24日<BR>4.&nbsp;美国新墨西哥州立大学“2+2双学位项目”<BR>项目简介：在本校和对方学校各学习两年，完成毕业要求后，拿双方学校本科学位。<BR>具体申请办法详见：<A href="http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=234">http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=234</A><BR>报名截止日期：2009年3月24日<BR>5.&nbsp;美国东新墨西哥大学“2+2双学位项目”<BR>项目简介：在本校和对方学校各学习两年，完成毕业要求后，拿双方学校本科学位。<BR>具体申请办法详见：<A href="http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=311">http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=311</A><BR>报名截止日期：2009年3月24日<BR>6.&nbsp;美国劳伦斯技术大学“2+2双学位项目”<BR>项目简介：在本校和对方学校各学习两年，完成毕业要求后，拿双方学校本科学位。<BR>具体申请办法详见：<A href="http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=312">http://www.oice.uestc.edu.cn/News/AbroadDetails.aspx?News_ID=312</A><BR>报名截止日期：2009年3月24日</P>\r\n<P><BR>联系人：国际处全老师（83202354）、教务处刘老师（83206631）</P>\r\n<P>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 国际合作与交流处<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二??九年三月十六日<BR></P>', 741, 1237166322, '2009-03-15 17:18:42', 0, NULL, NULL, NULL),
(163, 18, ' 北京信息控制研究所航天710所研究生调剂信息 ', '', NULL, 0, '1．需要计算机应用技术专业的调剂生源。总分335分以上，外语55分，数学110分以上男生。 \r\n<P>　　2．需要经济相关专业总分335分以上，外语55分，数学120分以上。</P>\r\n<P>　　<STRONG>联系电话</STRONG>：010-68372432、010-68768636&nbsp;&nbsp; 010-68372197&nbsp; 010-68372493 <BR>　　<STRONG>E-mail</STRONG>：<A href="mailto:zah_2001@tom.com" target=_blank>zah_2001@tom.com</A> <BR>　　<STRONG>地址</STRONG>：北京海淀区阜成路16号715室研究生招生办公室<BR>　　<STRONG>邮编</STRONG>：100037</P>\r\n<P>　　请将申请调剂表填写完后发到邮箱中，或邮寄过来。</P>\r\n<P>　　<STRONG>附件</STRONG>：<A class=text href="http://xiazai.eol.cn/kaoyan/bjxxkzyjstkyjssqtjb.doc" target=_blank><FONT color=#c00000>北京信息控制研究所统考研究生申请调剂表</FONT></A></P>', 2769, 1237168511, '2009-03-15 17:55:11', 0, NULL, NULL, NULL),
(164, 18, '信息产业部电子六所(华北计算机系统工程研究所)08年硕士研究生调剂信息', '', NULL, 0, '为了提高研究生招生质量，信息产业部电子六所尚有部分调剂名额，请希望调剂到我所读研的学生尽快与我所联系，具体信息如下：\r\n<P>　　<STRONG>一、调剂专业</STRONG></P>\r\n<P>　　1、081203 计算机应用技术(研究方向(1)信息安全(2)图形图像处理(3)嵌入式操作系统(4)移动3G及VOIP关键技术(5)管理与决策支持系统等10多个研究方向)</P>\r\n<P>　　<STRONG>二、时间安排</STRONG></P>\r\n<P>　　3月中旬至4月上旬接收调剂申请，按照北京市教委招办规定，可能在4月上旬开始复试。具体复试有关事项通知将于4月初发电子邮件或电话通知考生。</P>\r\n<P>　　<STRONG>三、需提交材料</STRONG></P>\r\n<P>　　1、准考证；</P>\r\n<P>　　2、报考材料(全国硕士生入学考试报名信息简表；初试试卷等相关材料)；</P>\r\n<P>　　3、毕业证书、学位证书(应届生带学生证)；</P>\r\n<P>　　4、大学期间成绩单原件或档案中成绩单复印件(加盖档案管理单位公章)；</P>\r\n<P>　　<STRONG>四、分数要求</STRONG></P>\r\n<P>　　总分&gt;=330分；单科过国家线；</P>\r\n<P>　　<STRONG>五、录取顺序</STRONG></P>\r\n<P>　　1、985和211学校的毕业生优先考虑录取；</P>\r\n<P>　　2、普通高等学校的毕业生按成绩先后顺序录取；</P>\r\n<P>　　<STRONG>六、联系方式</STRONG></P>\r\n<P>　　电话：010-82306057 82306043 <BR>　　联系人：郭老师 蒋老师 <BR>　　E-mail：<A href="mailto:guoyc@ncse.com.cn" target=_blank>guoyc@ncse.com.cn</A> <BR>　　邮政编码：100083<BR>　　地址：北京市海淀区清华东路25号</P>', 3385, 1237168540, '2009-03-15 17:55:40', 0, NULL, NULL, NULL),
(165, 18, '中国原子能科学研究院2008年招收硕士研究生调剂公告', '', NULL, 0, '<STRONG>招生单位名称</STRONG>：中国原子能科学研究院 \r\n<P>　　<STRONG>招生单位代码</STRONG>：82801</P>\r\n<P>　　<STRONG>调剂专业</STRONG>：070202&nbsp; 粒子物理与原子核物理<BR>　　&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 070302&nbsp; 分析化学<BR>&nbsp;　　&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 082701&nbsp; 核能科学与工程<BR>&nbsp;　　&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 082702&nbsp; 核燃料循环与材料</P>\r\n<P>　　<STRONG>需求专业</STRONG>：物理(核物理，物理学)<BR>　　　　　　　核工程与核技术<BR>　　　　　　　化学(放射化学，分析化学，化学工程，生物化学)<BR>　　　　　　　热能与动力工程</P>\r\n<P>　　<STRONG>是否有公费名额</STRONG>：有</P>\r\n<P>　　<STRONG>招生类别</STRONG>：统招统分</P>\r\n<P>　　<STRONG>联系方式</STRONG>：<BR>　　电话：(010)69357800 <BR>　　传真：(010)69357800<BR>　　E-mail： <A href="mailto:yjsk@ciae.ac.cn" target=_blank>yjsk@ciae.ac.cn</A><BR>　　联系人：孙晓颖</P>\r\n<P>　　<STRONG>详细调剂信息</STRONG>：</P>\r\n<P>　　一、我院2008年计划招生70人，现需要调剂考生约20人，拟调剂至我院的考生，可填写<STRONG>调剂信息登记表</STRONG>(见附件1)以及<STRONG>大学成绩单、个人简历</STRONG>发送到我院招生邮箱。国家分数线下达后，确需调剂的考生可登录中国研究生招生信息网申请调剂。此前无需寄送有关文字材料。</P>\r\n<P>　　二、调剂原则(如教育部下达分数线时有新规定，将以新规定为准)</P>\r\n<P>　　1、我院执行国家划定的A类分数线。<STRONG>调剂考生必须上原报考专业的A类分数</STRONG>线；<BR>　　2、统考科目必须与我院招生目录中的科目相同，专业课应相同或相近；<BR>　　3、核物理、核化学、核工程等涉核的专业以及211工程重点大学毕业生优先调剂。</P>\r\n<P>　　<STRONG>附件</STRONG>：<BR>　　附件1：<A href="http://xiazai.eol.cn/kaoyan/zgyznkxyjs08tjsqb.doc" target=_blank><FONT color=#c00000>2008年中国原子能科学研究院调剂申请表</FONT></A><BR>　　附件2：<A href="http://xiazai.eol.cn/kaoyan/zgyznkxyjs08sszsjz.doc" target=_blank><FONT color=#c00000>2008年中国原子能科学研究院研究生招生简章</FONT></A></P>\r\n<P>　　以上所填写信息务必属实，如果有误，所有责任考生自负。</P>\r\n<P>　　<STRONG>调剂注意事项</STRONG>：1、国家公布2008年复试分数基本要求之后，符合国家规定A类线考生可登陆中国研究生招生信息网(<A href="http://www.yz.chsi.com.cn/" target=_blank>www.yz.chsi.com.cn</A>)完成申请调剂程序，我院将尽快公布调剂名单，进入名单考生接到通知后应迅速与第一志愿报考学校研招办联系要求调剂到中国原子能科学研究院，并按要求将一志愿调剂表和报考试卷寄至我院。</P>', 3153, 1237168579, '2009-03-15 17:56:19', 0, NULL, NULL, NULL),
(166, 18, '天津大学软件学院软件工程硕士接收调剂', '', NULL, 0, '一、参加2008年全国统考研究生入学考试，凭初试成绩经天津大学软件学院面试考核后择优录取。转为软件工程硕士(单证)。\r\n<P>二、报名时请携带2008年全国硕士研究生考试成绩单(盖章)和电子数据条 。</P>\r\n<P>三、我院招收2008参加全国硕士统考的研究生(双证)除接收第一志愿报考我院的统考考生外，另接收其他学校相关专业统考考生转为软件工程硕士(双证)的调剂申请，学费3万元。调剂复试分数线为符合天津大学复试分数线。</P>\r\n<P>四、我院软件工程硕士自主招生报名正在热招中，欲报名者请登录：zyb.tju.edu.cn 在线报名并请查看招生简章。</P>\r\n<P>垂询电话：022-27402967</P>\r\n<P>传真：022-27405281</P>\r\n<P>联系人：马老师</P>\r\n<P>地点：天津市南开区卫津路92号天津大学第25教学楼B座602室</P>\r\n<P>邮编：300072</P>\r\n<P>天津大学研究生院</P>\r\n<P>天津大学国家示范性软件学院</P>', 3391, 1237168603, '2009-03-15 17:56:43', 0, NULL, NULL, NULL),
(167, 20, '学院鼓励学生参加出国培训考试的支持奖励办法', '', NULL, 0, '<P>&nbsp;&nbsp;&nbsp; 为鼓励我院学生树立远大志向，力争做高层次和国际化人才，经学院研究，对参加出国培训和考试的我院统招本科学生给予尽可能的帮助和支持，对取得良好成绩的同学给予奖励。<BR>1、&nbsp;对参加出国培训和考试的学生给予帮助。<BR>全院老师应为我院学生出国在信息、学校、专业、导师、推荐等方面给予尽可能的帮助。<BR>2、&nbsp;为参加出国培训和考试的学生提供方便。<BR>3、&nbsp;对参加出国培训和考试取得良好成绩的学生给予奖励。<BR>①&nbsp;大学本科四年中，我院统招本科学生参加出国培训和考试，英语成绩达到申请分数要求（当前为GRE达到1250分、托福90分或者雅思6.0），凭正式报名发票，学院给予全额报销其报名费一次。<BR>②&nbsp;大学本科四年中，我院统招本科学生参加出国培训和考试，英语成绩勉强达到个别院校申请分数要求（当前为GRE达到1200分、托福80分或者雅思5.5），凭正式报名发票，学院给予半额报销其报名费一次。<BR>4、&nbsp;鼓励英语四六级高分者参加出国培训考试。<BR>每年参加英语四六级考试全院成绩排名各前三位同学，学院鼓励其参加出国培训，在校本科四年中，学院可以直接为其支付一次出国英语考试（托福、GRE或雅思）报名费。<BR>应用数学学院<BR>2009.3.4</P>\r\n<P>（从2009年起开始试行）<BR><BR></P>', 3004, 1237199928, '2009-03-16 02:38:48', 0, NULL, NULL, NULL),
(168, 11, '学院鼓励学生参加出国培训考试的支持奖励办法', '', NULL, 0, '&nbsp;&nbsp;&nbsp; 为鼓励我院学生树立远大志向，力争做高层次和国际化人才，经学院研究，对参加出国培训和考试的我院统招本科学生给予尽可能的帮助和支持，对取得良好成绩的同学给予奖励。<BR>1、&nbsp;对参加出国培训和考试的学生给予帮助。<BR>全院老师应为我院学生出国在信息、学校、专业、导师、推荐等方面给予尽可能的帮助。<BR>2、&nbsp;为参加出国培训和考试的学生提供方便。<BR>3、&nbsp;对参加出国培训和考试取得良好成绩的学生给予奖励。<BR>①&nbsp;大学本科四年中，我院统招本科学生参加出国培训和考试，英语成绩达到申请分数要求（当前为GRE达到1250分、托福90分或者雅思6.0），凭正式报名发票，学院给予全额报销其报名费一次。<BR>②&nbsp;大学本科四年中，我院统招本科学生参加出国培训和考试，英语成绩勉强达到个别院校申请分数要求（当前为GRE达到1200分、托福80分或者雅思5.5），凭正式报名发票，学院给予半额报销其报名费一次。<BR>4、&nbsp;鼓励英语四六级高分者参加出国培训考试。<BR>每年参加英语四六级考试全院成绩排名各前三位同学，学院鼓励其参加出国培训，在校本科四年中，学院可以直接为其支付一次出国英语考试（托福、GRE或雅思）报名费。<BR>应用数学学院<BR>2009.3.4（从2009年起开始试行）<BR>', 862, 1237200022, '2009-03-16 02:40:22', 0, NULL, NULL, NULL),
(169, 15, '校第七届ACM/ICPC程序设计竞赛开始报名了', '', NULL, 0, '<P>一、参赛条件<BR>1．电子科技大学在读学生（包括本科生、专科生、在读硕士和博士）均可参赛。<BR>2．每支参赛队伍可由1~3名学生组成。<BR>二、报名办法<BR>报名时间3月19日――28日。请登陆<A href="http://www.math.uestc.edu.cn/acm/">http://www.math.uestc.edu.cn/acm/</A> 进行报名。组委会会向所有参赛队伍发送确认邮件。<BR>若有问题请致电13550332806或发邮件至<A href="mailto:lxhimo@163.com">lxhimo@163.com</A>或加入校内赛临时QQ群20621227与组委会联系。<BR>三、初赛<BR>1.&nbsp;初赛参赛语言为C/C++。<BR>2.&nbsp;时间：3月28号星期六上午9：00 ? 晚上9：00。<BR>3.&nbsp;初赛采用网络赛形式，采用的平台是天津大学在线裁判系统（以下简称TOJ）<A href="http://acm.tju.edu.cn/toj/">http://acm.tju.edu.cn/toj/</A>。<BR>4.&nbsp;初赛排名前40的队伍有机会晋级。<BR>5.&nbsp;初赛热身赛 初赛前会在TOJ上举办热身赛。时间是3月25号和3月27号。<BR>6.&nbsp;初赛及27号热身赛账号密码由组委会通过电子邮件统一发放，如到27号仍未收到邮件，请与组委会联系<BR>四、决赛<BR>1.&nbsp;比赛时间 4月4日星期六下午12：30 ? 17：30；地点 沙河校区 主楼东213和222。<BR>2.&nbsp;决赛会邀请来自西南地区高校的ACM/ICPC专业队伍参加。外校队伍不参与校内评奖。<BR>五、奖项设置<BR>&#61550;&nbsp;晋级决赛的队伍将获得纪念外套<BR>&#61550;&nbsp;一等奖： 2组&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二等奖： 5组&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 三等奖： 10组 <BR>六、学术讲座<BR>（一）3月23日晚7：00，清水河校区 A309<BR>（二）第二次讲座：时间地点待定&nbsp; </P>\r\n<P><BR>主办单位： 校教务处<BR>承&nbsp;&nbsp;&nbsp; 办： 应用数学学院<BR>协办单位： 校ACM协会<BR>合作企业：All Options（成都）有限公司</P>', 3168, 1237248654, '2009-03-16 16:10:54', 0, NULL, NULL, NULL),
(170, 20, '我校学生出国项目一览表', '', NULL, 0, '&nbsp;&nbsp;&nbsp; 学校大力支持学生出国深造，做国际化高层次人才。现将各种项目挂出，请大家注意。同时，希望大家随时关注学校“国际合作交流处”网站的相关内容。', 4025, 1237253199, '2009-03-16 17:26:39', 0, NULL, NULL, NULL),
(171, 22, '关于评审2009年度新增博士生指导教师的通知', '', NULL, 0, '根据国务院学位委员会关于下放博士生指导教师审批权的有关精神。我校2009年新增博士生指导教师评审工作即将开展，为保证新增博士生指导教师有较高的学术水平、良好的科研条件、严谨的治学态度和强烈的责任心，现将有关事项通知如下：<BR>1、我校的博士学位授权点均在申请之列。<BR>2、申报与评审的基本条件及有关程序，参见《电子科技大学自行审定博士生指导教师实施细则（试行）》。<BR>3、3月23日前申请人交1份《新担任博士生指导教师申请表》至刘燕老师处，《申请表》用A4纸，申请人应同时向学院提供有关成果（发表论文、获奖证书等）证明材料复印件（一式1份）。<BR>详见附件。<BR>', 1942, 1237260819, '2009-03-16 19:33:39', 0, NULL, NULL, NULL);
INSERT INTO `article` (`articleid`, `sortid`, `title`, `source`, `author`, `userid`, `content`, `click`, `publish_time`, `launch_time`, `imageid`, `keyword1`, `keyword2`, `keyword3`) VALUES
(172, 17, '数学和编程是什么关系（转载）', '', NULL, 0, '<H3 style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center" align=center><SPAN style="FONT-SIZE: 22pt"><FONT face=宋体>数学是一种思考方式<SPAN lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></FONT></SPAN></H3>\r\n<H3 style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center" align=center><SPAN lang=EN-US style="FONT-SIZE: 22pt"><o:p><FONT face=宋体>&nbsp;</FONT></o:p></SPAN></H3>\r\n<H3 style="TEXT-JUSTIFY: inter-ideograph; MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify"><FONT face=宋体><SPAN lang=EN-US style="FONT-SIZE: 10.5pt"><SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN><SPAN lang=EN-US style="FONT-SIZE: 12pt"><SPAN style="mso-spacerun: yes">&nbsp;</SPAN><SPAN style="mso-spacerun: yes">&nbsp;&nbsp;</SPAN></SPAN><SPAN style="FONT-SIZE: 12pt">本文作者系网易主力程序云风，参与《大话西游》《梦幻西游》等项目的制作。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></H3>\r\n<H3 style="TEXT-JUSTIFY: inter-ideograph; MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify"><SPAN lang=EN-US style="FONT-SIZE: 12pt"><o:p><FONT face=宋体>&nbsp;</FONT></o:p></SPAN></H3>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>这几天有个读者来来去去给我写了几封<SPAN lang=EN-US> email </SPAN>，问起我的观点：数学和编程是什么关系？学编程需要多深的数学基础？到底需要掌握哪些数学知识，对编程能力的提高有帮助。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>这个还真不好说。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>如果说起课堂上我们学到的知识。除了初等代数，在编程中我还真没碰到多少依赖数学技能来解决的问题。当年我学<SPAN lang=EN-US> C </SPAN>语言的时候很小，甚至不知道数学中函数这个概念，还不一样把<SPAN lang=EN-US> C </SPAN>语言学完了。虽然过了些年，我才把数学中的函数和程序中的函数联系起来。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>做<SPAN lang=EN-US> 3d </SPAN>游戏编程，大学里的线性代数可能还用的到一点。至少得知道矩阵运算吧。但是大部分程序员并不需要接触这些东西。除此之外，如果说程序员必须精通微积分才能编程，那绝对是鬼扯。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>如果对用编程的手段解决各种问题感兴趣，或许相关领域的数学知识有些用。比如我时常由于兴趣，做一些数据统计分析工作，这时概率统计的知识就少不了。但是解决问题，编程和数学一样，都只是工具。他们的地位是平等的，并非编程的技能依赖数学技能。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>那么，学习数学有用吗？当然有必要。因为数学是一种思考方式，编程需要这样的思考方式。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>我有个朋友，十年前认识他的时候，他说他在学佛。有一个问题没弄明白：佛说，不要执着。那么执着于不要执着是不是一种执着。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>过了好些年，他还没弄明白这个问题。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>学佛怎可以执着于这样的语言逻辑呢？光读佛经是没有用的，道理有时候需要顿悟。数学也如此。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>学习数学绝对不是无休止的解题训练，我们需要悟到其中的思考方法。那种逻辑严密的推理，对完美境界的构建。发现理论上的缺陷，并对其分析，重新构作。当然，这个过程，又需要我们做大量的练习，借此领悟其中的道理。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>编程也一样，不断的编写代码本身并不能直接提高编程能力。我们需要的是对问题的洞察力，构建系统的能力，理解机器运行时来龙去脉的能力，等等。而这些能力又是在不断编程实践中顿悟出来的。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>编程和数学一样，是绝对严谨的。程序有特定的输入，经过特定的流程，一定有特定的输出。对于程序中的<SPAN lang=EN-US> bug </SPAN>，我们不能抱有神秘论。它们不会没来由的出现，没来由的消逝。这就跟数学证明一样，逻辑或概念的缺陷不会随着时间消失，总等待后人来修补。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>另一方面，在数学中，直觉也是很重要的。直觉可以帮助我们快速理解问题，解决问题。对数学了解的越多，数学的直觉就越准。很多数学知识，不需要太多研究学习，靠直觉就可以理解了。程序写多了的人通常也会有这种感觉<SPAN lang=EN-US> :D<o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>只是有时候，数学中有些命题很直观，但是证明过程却非常繁杂。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>比如拓扑学中的若当曲线定理：平面上一条简单闭曲线<SPAN lang=EN-US> C </SPAN>恰好可以把平面分成两个区域，一个是内部，一个是外部。换句话说，平面上的点被分为了两类：在曲线外部的点集<SPAN lang=EN-US> A</SPAN>，和在曲线内部的点集<SPAN lang=EN-US> B </SPAN>。在同一点集中的任意两点都可以用一条不与<SPAN lang=EN-US> C </SPAN>相交的曲线相连，而连接一对不属于同一点集的两点的连线必然和<SPAN lang=EN-US> C </SPAN>相交。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>这个定理看起来很直观，显然是对的。但是一般人很难做出严格的数学证明，甚至很难看懂其证明。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>现代编程我们经常会遇到类似的东西。尤其在现在系统越来越复杂的情况下，一段很简单的应用程序代码，你可以很显然的知道它能做什么，但是很少有人可以完全解释清楚它是怎样一步步做到的。比如一段很简单的<SPAN lang=EN-US> Windows </SPAN>程序代码就是这样。只有拥有追根问底的数学精神，才会刨进操作系统底层，去搞明白系统到底怎样工作。这些不是一日之功，甚至不是三五年就够了的。而许多庸庸碌碌的程序员，满足于拖两个控件，粘合一下代码。最终只会感叹，程序只能写到三十岁。要我说，平庸资质如我，三十岁能入门就不错啦。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>数学的发展历史中，又包含了许多人无穷的创造力。光靠逻辑推理来一步步解决问题显然是不够的。很多数学问题的解决，都起源于某种直觉，某种创造性构建，甚至把许多表面不相关的东西牵连在一起思考。然后再通过逻辑严密的推导过程来完善它。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>例如，费马大定理的最终证明。首先找到了关于费马方程的解所在的费马曲线和椭圆曲线的联系，然后构造出一种特殊的椭圆曲线，当且仅当费马大定理不成立时，该曲线才存在。最后，通过证明这种曲线有一些极端奇怪和不可信的性质，决定了曲线不可能存在，从而反证了费马大定理成立。这之中虽然用到了许多前沿的高深数学理论成果才得以证明，但整个思路框架之巧妙，也是让人佩服之至。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>编程也是如此，我们首先需要对各种编程方法，编程语言的特性得心应手；而后，编程绝对不是简单的堆砌代码，它需要我们巧妙的构建系统，在合适的地方用合适的方式来解决问题。最终还需要让每个部分都严格正确。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.25pt; mso-char-indent-count: 2.5"><SPAN style="FONT-SIZE: 10.5pt"><FONT face=宋体>最后，对于那位朋友的问题，我只能给出我的个人建议。学习编程的确需要学习数学。但学习数学，不必列出书目，一本本去啃那些枯燥的教科书。只需要从数学史读起，弄清人类是怎样一步步理解数学的，学习其中的思想，最后用自己的兴趣去研究其中感到有趣的部分。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt"><SPAN lang=EN-US style="FONT-SIZE: 10.5pt"><FONT face=宋体>&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt"><SPAN lang=EN-US><o:p><FONT face="Times New Roman" size=3>&nbsp;</FONT></o:p></SPAN></P>', 3640, 1237358816, '2009-03-17 22:46:56', 0, NULL, NULL, NULL),
(173, 13, '党校第四十三期入党积极分子培训班教师、上课时间及教室安排', '', NULL, 0, '<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 150%; TEXT-ALIGN: center" align=center><SPAN style="FONT-SIZE: 15pt; LINE-HEIGHT: 150%; FONT-FAMILY: 黑体; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体">党校第四十三期入党积极分子培训班教师、上课时间及教室安排见附件！</SPAN><SPAN lang=EN-US style="FONT-SIZE: 15pt; LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; mso-bidi-font-family: 宋体"><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></P>', 1335, 1237425933, '2009-03-18 17:25:33', 0, NULL, NULL, NULL),
(174, 18, '中国地震局地震预测研究所2009年招收调剂生', '', NULL, 0, '<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center; mso-line-height-alt: 13.5pt" align=center><FONT color=#333333><B><SPAN style="FONT-SIZE: 15pt; COLOR: red; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-ascii-font-family: &#718;&#805;; mso-hansi-font-family: &#718;&#805;; mso-bidi-font-family: 宋体">中国地震局地震预测研究所</SPAN></B><B><SPAN lang=EN-US style="FONT-SIZE: 15pt; COLOR: red; FONT-FAMILY: &#718;&#805;; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体"><FONT face="Times New Roman">09</FONT></SPAN></B><B><SPAN style="FONT-SIZE: 15pt; COLOR: red; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-ascii-font-family: &#718;&#805;; mso-hansi-font-family: &#718;&#805;; mso-bidi-font-family: 宋体">年招收若干名调剂生，申请调剂的考生应符合下列条件：</SPAN></B><B><SPAN lang=EN-US style="FONT-SIZE: 15pt; COLOR: red; FONT-FAMILY: &#718;&#805;; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体"><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></B></FONT></P>\r\n<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt; mso-line-height-alt: 13.5pt"><FONT color=#333333><B><SPAN lang=EN-US style="FONT-SIZE: 15pt; COLOR: red; FONT-FAMILY: &#718;&#805;; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体"><FONT face="Times New Roman">&nbsp;&nbsp;&nbsp; 1. </FONT></SPAN></B><B><SPAN style="FONT-SIZE: 15pt; COLOR: red; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-ascii-font-family: &#718;&#805;; mso-hansi-font-family: &#718;&#805;; mso-bidi-font-family: 宋体">国家</SPAN></B><B><SPAN lang=EN-US style="FONT-SIZE: 15pt; COLOR: red; FONT-FAMILY: &#718;&#805;; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体"><FONT face="Times New Roman">“<?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:chmetcnv w:st="on" TCSC="0" NumberType="1" Negative="False" HasSpace="False" SourceValue="211" UnitName="”">211”</st1:chmetcnv></FONT></SPAN></B><B><SPAN style="FONT-SIZE: 15pt; COLOR: red; FONT-FAMILY: 宋体; mso-font-kerning