INSERT INTO `article` (`articleid`, `sortid`, `title`, `source`, `author`, `userid`, `content`, `click`, `publish_time`, `launch_time`, `imageid`, `keyword1`, `keyword2`, `keyword3`) VALUES
(252, 11, '关于2009年暑期社会实践的通知', '', NULL, 0, '<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 21pt; MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">今年暑期组织全校学生开展志愿者“三下乡”暑期社会实践活动，现将暑期社会实践工作的相关事宜通知如下：<SPAN lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">一、主要内容<SPAN lang=EN-US><SPAN style="mso-spacerun: yes">&nbsp; </SPAN><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="LINE-HEIGHT: 150%"><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体" lang=EN-US>1</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体">、深入灾区，参与灾后重建<SPAN lang=EN-US> <o:p></o:p></SPAN></SPAN></B></FONT></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; mso-char-indent-count: 2.0"><FONT size=3><SPAN style="FONT-FAMILY: 宋体">组织学生深入灾区积极参与灾后重建，开展</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-font-kerning: 0pt">结对帮扶、义务支教、物资清理、政策宣传、心理疏导等灾区人民需要的切实有效的服务活动。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt" lang=EN-US>2</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt">、</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">关爱留守儿童，服务社会主义新农村<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">组织学生深入农村外出务工家庭，了解留守学生情况。针对留守学生的实际困难，为留守学生开展心理辅导、文化补习、赠送文化用品等切实有效的服务，帮助他们减轻学习和生活上的困难。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt" lang=EN-US>3</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">、开展“三下乡”活动，共建和谐社会</SPAN></B><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 21pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体">以党的十七大精神为指导，组织科技、文化、卫生“三下乡”活动，通过实践服务、企业帮扶、法律政策宣讲、支教扶贫、环境保护等多种形式，深入了解和参与和谐社会建设。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt" lang=EN-US>4</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">、开展</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt">“四进社区”活动，协调基层社会发展</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt" lang=EN-US><o:p></o:p></SPAN></B></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体">组织学生开展科教、文体、法律、卫生“四进社区”实践活动。深入城市社区、乡镇、农村进行调查研究、扶贫助残、义务家教、文化服务等活动。</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt" lang=EN-US>5</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt">、</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">发挥专业优势，理论与实践相结合<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 28.5pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt">积极鼓励广大学生结合自身的专业特色，发挥学科优势，开展各类与专业相关的实习、课程调研、项目攻关等实践活动，把专业特色与社会实践相结合，促进理论与实践的有机结合，提高专业知识和技能。</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%"><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体" lang=EN-US>6</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体">、其他</SPAN></B><SPAN style="FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体" lang=EN-US><o:p></o:p></SPAN></FONT></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体">二、活动方式<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 23.5pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 1.96" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt" lang=EN-US>1</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">、采取分散与集中相结合的方式进行，学生可选择参加由学校或学院组织的社会实践团队，也可自行开展校外社会实践活动（<B style="mso-bidi-font-weight: normal">组队</B>），学院组队内容可根据今年暑期社会实践主题开展，或结合学院专业特色开展实践活动。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24.7pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.06" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt" lang=EN-US>2</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">、各同学先期联系实践基地，并确定队伍开展活动的相关细节，向学院报送《电子科技大学<SPAN lang=EN-US>2009</SPAN>年学生暑期社会实践项目申报书》。对于学院重点关注且围绕本年社会实践主题开展活动的团队，可在项目申报书中申请不同的资助金等级（金额按照<SPAN lang=EN-US>A</SPAN>、<SPAN lang=EN-US>B</SPAN>、<SPAN lang=EN-US>C</SPAN>、<SPAN lang=EN-US>D</SPAN>、<SPAN lang=EN-US>E</SPAN>五级逐步递减）。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt" lang=EN-US>3</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">、采取学校、学院共同出资的原则，校团委根据《电子科技大学<SPAN lang=EN-US>2009</SPAN>年学生暑期社会实践项目申报书》组织开展评审会，确定不同团队的不同资助等级。予以资助的实践团队按实际情况进行预拨款，实践活动结束后根据完成效果拨付余款。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">三、实施步骤<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">（一）实施阶段<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt" lang=EN-US>7</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">月至<SPAN lang=EN-US>8</SPAN>月，各社会实践团队和参加社会实践的同学按照学校要求和实践方案奔赴实践地，积极开展社会实践活动，力求务实创新，出成果、出效益。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">（二）总结阶段<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 21pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt" lang=EN-US>9</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">月下旬，参加社会实践的队伍和个人在活动结束后及时进行总结，在规定时间内将鉴定表、团队总结、<SPAN lang=EN-US>DV</SPAN>等相关材料按要求上报分团委或校团委。<B style="mso-bidi-font-weight: normal"><SPAN lang=EN-US><o:p></o:p></SPAN></B></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">（三）表彰阶段<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">学校将对优秀社会实践成果编辑成册，并召开表彰大会对社会实践活动中涌现出来的典型事迹、先进集体、优秀指导教师、优秀个人予以表彰。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 15pt">五、说明</SPAN></B><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 15pt">：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 12pt" lang=EN-US>1</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 12pt">、学院现有经过选拔的三泰电子暑期社会实践队和志愿者暑期夏令营实践队，其他同学要参加暑期社会实践项目申报的请自行组队，每支队伍至少<SPAN lang=EN-US>3</SPAN>人以上。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 12pt" lang=EN-US>2</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 12pt">、暑期社会实践项目申报书于<?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:chsdate w:st="on" IsROCDate="False" IsLunarDate="False" Day="12" Month="6" Year="2009"><SPAN lang=EN-US>2009</SPAN>年<SPAN lang=EN-US>6</SPAN>月<SPAN lang=EN-US>12</SPAN>日</st1:chsdate>前上报应用数学学院分团委。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 12pt" lang=EN-US>3</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 12pt">、暑期社会实践项目申报书需上交电子档和书面档，邮件请发送至：<SPAN lang=EN-US>yepy2007@126.com</SPAN>，纸质档于上班时间交到活动中心<SPAN lang=EN-US>211</SPAN>房间。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 12pt" lang=EN-US>4</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 12pt">、暑期社会实践解释权最终归应用数学学院分团委，咨询电话：<SPAN lang=EN-US>13551034135.<o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">附<SPAN lang=EN-US><SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN>件：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">一、电子科技大学<SPAN lang=EN-US>2009</SPAN>年学生暑期社会实践项目申报书<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">二、电子科技大学<SPAN lang=EN-US>2009</SPAN>年学生暑期社会实践经费资助报销制度<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">三、电子科技大学<SPAN lang=EN-US>2009</SPAN>年学生暑期社会实践评奖办法<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">四、电子科技大学<SPAN lang=EN-US>2009</SPAN>年学生暑期社会实践安全责任书<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">五、电子科技大学<SPAN lang=EN-US>2009</SPAN>年学生暑期社会实践团队守则<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt">六、电子科技大学<SPAN lang=EN-US>2009</SPAN>年学生暑期社会实践队报账明细表<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>', 829, 1243737406, '2009-05-30 18:36:46', 0, NULL, NULL, NULL),
(255, 9, '"职工货币化住房补贴信息核对"的通知', '', NULL, 0, '<P>全院教职员：<BR>&nbsp;&nbsp;&nbsp;&nbsp; 应学校后勤管理处的要求，我院落实学校“职工货币化住房补贴”的工作进行<BR>到第二阶段。现在需要全院在职的教职员工于2009年6月10日前（周末除外）到院</P>\r\n<P>办核对你的信息并签字。</P>\r\n<P>本次“职工货币化住房补贴”关系每个教职员工的切身利益，请你一定要来！</P>\r\n<P>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 数学学院办公室<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2009.6.1 </P>', 2522, 1243848104, '2009-06-01 01:21:44', 0, NULL, NULL, NULL),
(256, 11, '学院对毕业生进行职前和诚信教育', '', NULL, 0, '<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=163" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=162" align=baseline border=0></P>\r\n<P>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 立言厉行 诚信为首<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ――数学学院2005级毕业生职前诚信教育大会<BR>&nbsp;&nbsp;&nbsp; 2005级学生即将离开学校，进入新的学习、工作阶段。在离校前夕，学院召开了全年级的诚信教育暨职前教育大会，05级在校同学全体参会，苟书记应邀到会并作重要讲话。<BR>&nbsp;&nbsp;&nbsp; 首先，辅导员张娇老师与大家分享了她自身学习的心得，从心态、行动、立世根本及培养成功者的能力几个方面与大家进行了交流，希望同学们诚信为本，把握好心态，注意待人处事的技巧，在已经选定的道路上全力以赴，相信自己，不断地挖掘潜能，提升自己。之后，张老师把6月的毕业生活动安排及进展情况对同学们进行了详细说明，再次强调安全问题，并充分调动大家的积极性，合力为本科阶段的生活再添风采。<BR>&nbsp;&nbsp;&nbsp; 作为会议的另一主要内容，张老师就毕业生还款确认相关事项和时间安排、应征入伍毕业生及去西部和艰苦地区就业的大学生享受国家助学代偿等工作进行了细致讲解，发放2009年助学贷款学生毕业还款的相关资料，要求大家仔细阅读，重要的细节做了提示，使同学们对国家助学贷款的相关政策及展期、还款中需注意的事项有了深刻的理解，认识到保持良好的信用记录的重要性。<BR>&nbsp;&nbsp;&nbsp; 会上，苟书记对即将步入职场的全体准毕业生们进行了职前教育，他从身边的人、事讲起，细述同学们日后发展中应有意识去培养的个人素质和能力，向大家提出务必诚信求真的殷切希望。针对毕业生系列活动，苟书记从整体部署和细节完善方面做出指示，强调了毕业信息填写和核准的重要性。<BR></P>', 732, 1243908608, '2009-06-01 18:10:08', 0, NULL, NULL, NULL),
(257, 11, '泛珠三角大学生计算机作品赛通知', '', NULL, 0, '<P>&nbsp;&nbsp;&nbsp; 由云南省计算机学会主办, 四川、云南、广东、广西、湖南、江西、福建和海南等8省计算机学会与香港互联网专业人员协会、澳门电脑学会共同协办，安利（中国）日用品有限公司冠名赞助的泛珠三角（9+2）大学生计算机作品赛将于2009年6月底在昆明云南大学举行展示和决赛。这是一次为泛珠三角区域大学的计算机专业学生提供一个良好的交流平台，充分展示泛珠三角大学计算机专业的教学成果，推动内外合作、面向应用、面向市场，增加各界对计算机人才了解的机会，达到各地师生“展示交流、增进友谊、互相切磋、共同提高”的目的，从而促进泛珠三角区域计算机教育事业的共同发展。</P>\r\n<P>&nbsp;&nbsp;&nbsp; 根据安排，四川省是一个分赛区，将通过分赛区举行的预选赛评选出四件作品参加云南大学决赛和展示。为此，特邀请贵校组织本科应届毕业生完成的作品（毕业设计或毕业论文）参加预选赛。有关事项通知如下</P>\r\n<P>&nbsp;</P>', 1124, 1243929852, '2009-06-02 00:04:12', 0, NULL, NULL, NULL),
(258, 11, '德意志学术交流中心（DAAD）“留学德国”讲座通知', '', NULL, 0, '<P>主题：<BR>1. 留学德国攻读博士<BR>2. DAAD&nbsp;奖学金项目</P>\r\n<P>时间：2009年6月10日下午15：00-16：00<BR>地点：沙河校区二教楼106教室<BR>&nbsp;<BR>报告人：</P>\r\n<P>郑怡--德国波恩大学翻译学毕业，DAAD&nbsp;驻京办事处奖学金项目负责人</P>\r\n<P>关于&nbsp;DAAD&nbsp;：<BR>&nbsp;<BR>德意志学术交流中心简称&nbsp;DAAD,&nbsp;最早成立于&nbsp;1925&nbsp;年，代表德国&nbsp;231&nbsp;所高校和<BR>128&nbsp;个大学生团体，是目前全球最大的教育交流机构之一。&nbsp;DAAD&nbsp;的经费由德国<BR>政府提供，是德国文化和高等教育政策的对外执行机构。<BR>&nbsp;<BR>作为德国高等院校的联合组织，德意志学术交流中心的主要任务是扶持德国和其他国家大学生、科学家的交换项目以及国际科研项目，并以此来促进德国大学同国外大学的联系。它拥有一个广泛的由德语讲师、分支机构以及前奖学金生组成的网络。目前已有大约&nbsp;68,000&nbsp;名大学生和科学家接受过&nbsp;DAAD&nbsp;的资助，将近&nbsp;500&nbsp;名大学教授被派往世界各地授课。&nbsp;目前，德意志学术交流中心在世界范围内设立了14&nbsp;个办事处，为当地提供信息和咨询。北京代表处成立于&nbsp;1994&nbsp;年，主要与中国教育部和国家留学基金委合作，为中国高校和中德人员交流提供资助。<BR></P>', 1205, 1244166659, '2009-06-04 17:50:59', 0, NULL, NULL, NULL),
(259, 10, '2005级毕业论文装订通知', '', NULL, 0, '2005级毕业论文装订通知', 945, 1244180847, '2009-06-04 21:47:27', 0, NULL, NULL, NULL),
(260, 11, '关于评选2009届校级优秀毕业生的通知', '', NULL, 0, '<P style="LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">各位同学：<SPAN lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>2009</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">届校级优秀毕业生的评选工作已经开始，具体安排如下：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">评选条件：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>1.</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">坚持四项基本原则，拥护党的路线、方针、政策，政治上积极要求进步，品德优良，关心和团结同学，积极参加学校和班集体组织的活动，遵守学校的各项规章制度，发挥模范带头作用；<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>2.</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">在校期间两次获得二等以上奖学金（含国家奖学金和专项奖学金，大三学年荣获奖学金按照学院公示名单确定）或被评为四川省三好学生或优秀学生干部一次以上；<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>3.</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">学习目的明确，态度端正，认真刻苦，大学期间所有基础性素质所含科目均及格；<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>4.</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">积极参加文体活动，身体健康，达到并保持《国家体育锻炼标准》；<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>5.</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">在校期间，未受过纪律处分。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">评选注意事项：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>1</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">．优秀毕业生的评定按照公开、公平、公正的原则进行评选；<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>2</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">．我院校优秀毕业生评选名额为10人，省级优秀毕业生不再参加本次评选。</SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt"></SPAN></B>&nbsp;</P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal align=left><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">评选安排：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>请符合评选条件且有意申请的同学于</SPAN><?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:chsdate Year="2008" Month="6" Day="13" IsLunarDate="False" IsROCDate="False" w:st="on"><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>6</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">月<SPAN lang=EN-US>12</SPAN>日</SPAN></st1:chsdate><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">下午<SPAN lang=EN-US>5</SPAN>点前填好相关材料（<SPAN lang=EN-US>2009</SPAN>届优秀毕业生备案表、校优申请表），将备案表的电子档和申请表的纸质档交到办公室。</SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt"></SPAN>&nbsp;</P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 应用数学学院学生科</SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 252pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 21.0" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt" lang=EN-US>2009</SPAN><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 仿宋_GB2312; FONT-SIZE: 12pt">年<SPAN lang=EN-US>6</SPAN>月9日<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>', 1513, 1244539769, '2009-06-09 01:29:29', 0, NULL, NULL, NULL),
(261, 19, '转“成都出台17政策促就业 大学生创业补贴5000元”', '', NULL, 0, '<P style="TEXT-INDENT: 2em"><STRONG>成都市出台17条政策，促进普通高等学校毕业生创业就业</STRONG></P>\r\n<P style="TEXT-INDENT: 2em">成都市将实施“千名高校毕业生创业扶持计划”，计划2009年到2010年，重点扶持1500名高校毕业生自主创业。9日，成都市人事局副局长罗建民宣布了成都市最新出台的17条促进普通高等学校毕业生创业就业的政策。此外，成都市还在全国范围内第一个提出了“一村两大”的计划，今年拟招募6700名高校毕业生到基层就业，其中包括2600名“村官”。</P>\r\n<P style="TEXT-INDENT: 2em"><STRONG>创业补贴</STRONG></P>\r\n<P>　　按照政策，高校毕业生在成都创业成功（自工商登记注册之日起3个月内正常经营）的，一次性给予5000元创业补贴。高校毕业生创办的企业，招用首次就业的高校毕业生，签订1年以上期限劳动合同并缴纳社会保险费的，每招用1名高校毕业生，一次性给予1000元岗位补贴</P>\r\n<P>　　<STRONG>贷款贴息</STRONG></P>\r\n<P>　　在资金扶持方面，高校毕业生在成都自主创业自筹资金不足的，可申请不超过5万元小额担保贷款；合伙经营和组织起来就业的，可据实际人数放宽到20万元。贷款3年内由政府全额贴息。据悉，针对的高校毕业生并不局限于本地，而是面向全国范围</P>\r\n<P>　　<STRONG>基层就业</STRONG></P>\r\n<P>　　“今年年内要招2600名村官，在全国范围内第一个实施‘一村两大’的计划。”今年成都市将实施“一村（涉农社区）两名大学生计划”、“农村中小学特设教师岗位计划”、“乡（镇）公立卫生院大学生支医计划”和“大学生服务社区就业和社会保障计划”等</P>\r\n<P>　　<STRONG>就业援助</STRONG></P>\r\n<P>　　成都将对享受城乡居民最低生活保障、零就业、地震受灾严重等困难家庭的本市生源高校毕业生开展“一对一”就业援助服务，确保有就业意愿的困难家庭高校毕业生当年全部实现就业</P>', 2702, 1244597820, '2009-06-09 17:37:00', 0, NULL, NULL, NULL),
