INSERT INTO `article` (`articleid`, `sortid`, `title`, `source`, `author`, `userid`, `content`, `click`, `publish_time`, `launch_time`, `imageid`, `keyword1`, `keyword2`, `keyword3`) VALUES
(472, 18, '香港理工大学-香港浸会大学2010学年硕士招生', '', NULL, 0, '<P style="TEXT-INDENT: 15.75pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.5" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>2010</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">年香港理工大学、香港浸会大学全日制硕士招生申请已接近尾声，如果即将毕业的同学有意向想去香港读研，请抓紧时间，把握机会，准备申请吧。<SPAN lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: -21pt; MARGIN: 0cm 0cm 0pt 21pt; mso-list: l0 level1 lfo1; tab-stops: list 21.0pt" class=MsoNormal><SPAN style="FONT-FAMILY: Wingdings; COLOR: red; mso-bidi-font-size: 10.5pt; mso-fareast-font-family: Wingdings; mso-bidi-font-family: Wingdings" lang=EN-US><SPAN style="mso-list: Ignore"><FONT size=3>l</FONT><SPAN style="FONT: 7pt  Times New Roman ">&nbsp; </SPAN></SPAN></SPAN><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; COLOR: red; mso-bidi-font-size: 10.5pt">香港读研的优势</SPAN></B><SPAN style="FONT-FAMILY: 宋体; COLOR: red; mso-bidi-font-size: 10.5pt">： <SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 15.8pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.5" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">第一</SPAN></B><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">，香港是中西文化交汇的地区，读硕士最大的优势在于免试入学，学生不用远赴海外就可以感受不同的学习背景及文化特色，增长见识，开阔视野。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 15.8pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.5" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">第二</SPAN></B><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">，学习年限上比内地高校时间短，是一年全日制，学习的内容很实用，能切实应用到工作当中。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 15.8pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.5" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">第三</SPAN></B><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">，香港高校的硕士学位受到很多国家教育机构承认，获得硕士学位的同学可以选择去海外继续深造攻读博士学位。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 15.8pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.5" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">第四</SPAN></B><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">，香港与内地互认高等教育文凭。教育部承认香港八所大学本科以上文凭。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 15.8pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.5" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">第五</SPAN></B><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">，凡向入境事务处提出申请的应届非本地毕业生，一律可以无条件留港，逗留期限为<SPAN lang=EN-US>12</SPAN>个月。在这段期间他们可以随意就业。其后提出的延长逗留期限申请。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; COLOR: red; mso-bidi-font-size: 10.5pt"><FONT size=3>一．香港理工大学专业介绍<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>机械工程系<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>1.</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">机械工程学理学硕士<SPAN lang=EN-US><SPAN style="mso-tab-count: 1">&nbsp; </SPAN><o:p></o:p></SPAN></SPAN></B></FONT></P>\r\n<P style="TEXT-INDENT: 10.5pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>机械工程学理学硕士<SPAN lang=EN-US>(</SPAN>产品开发及分析方向<SPAN lang=EN-US>)<SPAN style="mso-spacerun: yes">&nbsp; </SPAN><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="TEXT-INDENT: 10.5pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>机械工程学理学硕士<SPAN lang=EN-US>(</SPAN>空气<SPAN lang=EN-US> / </SPAN>噪音环保管理方向<SPAN lang=EN-US>)<SPAN style="mso-spacerun: yes">&nbsp; </SPAN><o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>2.</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">汽车工程设计理学硕士<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>电机工程系<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>1.</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">电机工程学理学硕士<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US><SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">电机工程学理学硕士<SPAN lang=EN-US>(</SPAN>电力系统方向<SPAN lang=EN-US>)<o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US><SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">电机工程学理学硕士<SPAN lang=EN-US>(</SPAN>电力电子及驱动方向<SPAN lang=EN-US>)<o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 10.5pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>电机工程学理学硕士<SPAN lang=EN-US>(</SPAN>铁路系统方向<SPAN lang=EN-US>)<o:p></o:p></SPAN></FONT></SPAN></P>\r\n<P style="TEXT-INDENT: 10.5pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; COLOR: red; mso-bidi-font-size: 10.5pt"><FONT size=3>二．香港浸会大学专业介绍<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>理学院<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>1.</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">运筹学及商业统计学理学双硕士<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>2.</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">分析化学理学硕士<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>3.</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">环境及公共卫生管理理学硕士<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>4.</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">信息科技管理理学硕士<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>5.</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">绿色能源科技理学硕士<SPAN style="COLOR: red">（正在审批中）</SPAN><SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>工商管理学院<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>1.</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">工商管理硕士<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>文学院<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>1.</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">翻译及双语传意文学硕士<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>◆</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">申请资格：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></FONT></P>\r\n<P style="TEXT-INDENT: 15.8pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 1.5" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>拥有教育部认可的大学或大专院校所颁发的理科、工科、商科或相关学科的学士学位或同等学历要求申请人的大学平均成绩良好，具有较好的英语水平（需提供<SPAN lang=EN-US>TOEFL</SPAN>或<SPAN lang=EN-US>IELTS</SPAN>或国家英语考试其中任一项成绩证明）。<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>联系方式：<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>香港高校内地申请处<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>亚太管理学院<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt"><FONT size=3>地址<SPAN lang=EN-US>:</SPAN>北京市海淀区中关村大街甲<SPAN lang=EN-US>59</SPAN>号 文化大厦<SPAN lang=EN-US>1709 </SPAN>室（人大东门北侧）<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></SPAN></B></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">咨询电话</SPAN></B><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US>:<B style="mso-bidi-font-weight: normal">010-62510880</B></SPAN><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">、<SPAN lang=EN-US>62519559<SPAN style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp; </SPAN></SPAN>传真<SPAN lang=EN-US>:010-62516588<o:p></o:p></SPAN></SPAN></B></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">申请表索取</SPAN></B><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt" lang=EN-US><A href="email:%20apimcbj@gmail.com"><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="COLOR: windowtext; TEXT-DECORATION: none; text-underline: none">Email: </SPAN></B><FONT color=#0000ff>apimcbj@gmail.com</FONT></FONT></A><o:p></o:p></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">详情请浏览内地申请处网站</SPAN></B><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">： <SPAN lang=EN-US><A href="http://www.math.uestc.edu.cn/09-10网络宣传/www.apimc.cn"><FONT color=#0000ff>www.apimc.cn</FONT></A><o:p></o:p></SPAN></SPAN></FONT></P>', 2857, 1273457405, '2010-05-09 18:10:05', 0, NULL, NULL, NULL),
(473, 17, '数学科学学院2010届毕业生文化衫方案', '', NULL, 0, '<SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 16pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ; mso-font-kerning: 1.0pt; mso-bidi-font-family:  Times New Roman ; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT color=#ff0000 size=3>设计效果图一</FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><IMG border=0 hspace=0 alt="" align=baseline src="http://www.math.uestc.edu.cn//showimg.php?iid=329"></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">&nbsp;&nbsp;&nbsp; </SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 正面</SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><IMG border=0 hspace=0 alt="" align=baseline src="http://www.math.uestc.edu.cn//showimg.php?iid=324"></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 89.25pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 8.5" class=MsoNormal><FONT size=3><SPAN lang=EN-US><FONT face="Times New Roman"><SPAN style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN><SPAN style="mso-spacerun: yes">&nbsp;&nbsp;</SPAN><SPAN style="mso-spacerun: yes">&nbsp;</SPAN></FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">背面</SPAN><SPAN style="mso-bidi-font-size: 10.5pt" lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3>具体说明：</FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt 21pt; mso-para-margin-left: 2.0gd" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">图案解释：</SPAN></B><FONT face="Times New Roman"> </FONT><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">我们是一艘舰船，是</SPAN><SPAN lang=EN-US><FONT face="Times New Roman">future</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">号，上面用了半透明的方式做了烟囱，虚线是我们数学中常用的元素，我们的生活环境架设在坐标上，跨过像函数一样的波浪，从</SPAN><SPAN lang=EN-US><FONT face="Times New Roman">2006</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">年到</SPAN><SPAN lang=EN-US><FONT face="Times New Roman">2010</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">年，向着我们数学人的未来前进。</SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt 21pt; mso-para-margin-left: 2.0gd" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">思路：</SPAN></B><FONT face="Times New Roman"> </FONT><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">用了比较多的数学元素，背面也用了英文的名称和卡通字体的标语，显得比较清新。缺点是颜色过多，可能要消减一些，改换成其他的颜色。</SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN lang=EN-US><o:p><FONT size=3 face="Times New Roman">&nbsp;</FONT></o:p></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN lang=EN-US><o:p><FONT size=3 face="Times New Roman"></FONT></o:p></SPAN>&nbsp;</P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT color=#ff0000 size=3>设计效果图二</FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT color=#ff0000 size=3></FONT></SPAN>&nbsp;</P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><IMG border=0 hspace=0 alt="" align=baseline src="http://www.math.uestc.edu.cn//showimg.php?iid=328"></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 正面</FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "></SPAN>&nbsp;</P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><FONT size=3><SPAN lang=EN-US><FONT face="Times New Roman"><SPAN style="mso-spacerun: yes"><IMG border=0 hspace=0 alt="" align=baseline src="http://www.math.uestc.edu.cn//showimg.php?iid=325"></SPAN></FONT></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 89.25pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 8.5" class=MsoNormal><FONT size=3><SPAN lang=EN-US><FONT face="Times New Roman"><SPAN style="mso-spacerun: yes"></SPAN></FONT></SPAN></FONT><FONT size=3><SPAN lang=EN-US><FONT face="Times New Roman"><SPAN style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN></FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">背面</SPAN><SPAN style="mso-bidi-font-size: 10.5pt" lang=EN-US><o:p></o:p></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3>具体说明：</FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt 21pt; mso-para-margin-left: 2.0gd" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">图案解释：</SPAN></B><FONT face="Times New Roman"> </FONT><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">采用的了透视的方法，在人生的坐标中，我们在大学中走过人生的一段重要的路程，右下角的脚代表我们自己，那个羽毛是一只笔，写下了我们的</SPAN><SPAN lang=EN-US><FONT face="Times New Roman">future</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">。背面是简介的中文介绍，没有加过多的描述。</SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt 21pt; mso-para-margin-left: 2.0gd" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">思路：</SPAN></B><FONT face="Times New Roman"> </FONT><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">透视方法是亮点，缺点是羽毛有颜色渐变，不太好实现（成本问题），图案有些简单，数学元素较少。</SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3></FONT></SPAN>&nbsp;</P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3></FONT></SPAN>&nbsp;</P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT color=#ff0000 size=3>设计效果图三</FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><IMG border=0 hspace=0 alt="" align=baseline src="http://www.math.uestc.edu.cn//showimg.php?iid=327"></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;正面</FONT></SPAN></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><IMG border=0 hspace=0 alt="" align=baseline src="http://www.math.uestc.edu.cn//showimg.php?iid=326"></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="mso-bidi-font-size: 10.5pt; mso-no-proof: yes" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="TEXT-INDENT: 89.25pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 8.5" class=MsoNormal><FONT size=3><SPAN lang=EN-US><FONT face="Times New Roman"><SPAN style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN></FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">背面</SPAN><SPAN style="mso-bidi-font-size: 10.5pt" lang=EN-US><o:p></o:p></SPAN></FONT></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3>具体说明：</FONT></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt 21pt; mso-para-margin-left: 2.0gd" class=MsoNormal><FONT size=3><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">图案解释：</SPAN></B><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">这是在第二个方案上做的改进，坐标换了三个很有特色的形态，用的灰色调，比较洒脱，却并不喧宾夺主，笔改成了简单的红色羽毛笔，写下了一个数学公式，我们一路写过</SPAN><SPAN lang=EN-US><FONT face="Times New Roman">2006</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">到</SPAN><SPAN lang=EN-US><FONT face="Times New Roman">2010,</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">，未来还有很长的路要我们去探索。背面用了简约的方式，用了篆体，加上淡淡的浮云，简洁明快。</SPAN></FONT></P>\r\n<P><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 10.5pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ; mso-bidi-font-size: 12.0pt; mso-font-kerning: 1.0pt; mso-bidi-font-family:  Times New Roman ; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">思路：</SPAN></B><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 10.5pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ; mso-bidi-font-size: 12.0pt; mso-font-kerning: 1.0pt; mso-bidi-font-family:  Times New Roman ; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">比较简洁，用了四种颜色，可行性比较强，色调和风格比较统一。</SPAN></SPAN></P>', 2113, 1273482630, '2010-05-10 01:10:30', 0, NULL, NULL, NULL),
(474, 10, '数学科学学院本专业重新学习考试安排', '', NULL, 0, '数学科学学院本专业重新学习考试安排', 1233, 1273560295, '2010-05-10 22:44:55', 0, NULL, NULL, NULL),
(475, 11, '“一张卡片,一个愿望,一份希望”爱心募捐活动', '', NULL, 0, '<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=332" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=334" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=333" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=331" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=330" align=baseline border=0>&nbsp;&nbsp;&nbsp;</P>\r\n<P>&nbsp;&nbsp;&nbsp;&nbsp; 当我们坐在宽敞的教室里上课时，当我们还在报怨食堂里的饭不好吃时，我们是否想到了那些身在灾区的小朋友？他们本应该和我们一样过着幸福健康的生活，可是就因为两年前的那场地震，一切都变了。尽管现在距那次惊心动魄的大地震已过去了整整两年，但当时的那些情形却在他们的心灵深处留下了深深的阴影。为了给远在灾区的小朋友送去我们的一片爱心，在汶川大地震2周年之际，我院08级党支部联合maple志愿者团队一起携手1kg志愿者团队进行了“一张卡片,一个愿望,一份希望”爱心募捐活动。<BR>&nbsp;&nbsp;&nbsp; 5月11日下午和12日中午，在银桦餐厅门口前，我们进行了此次募捐活动。活动过程中，我们展示了1kg志愿者团队近几年来在各个地方进行的公益活动，同时也用借助横幅和海报的来宣传我们此次活动的目的。活动过程中，不断有同学前来奉献自己的一片爱心，“益智屋”、“小学生报”，藏族小朋友“汉语学习班”，这是贫困山区的孩子们的简单愿望，我们用同学们的爱心捐助去实现小朋友们的愿望，给他们带去温暖和关爱。对每位捐助者，我们赠送纪念礼品——贫困孩子们的笑脸，也向前来募捐的同学赠送了爱心回馈明信片，并请他们留下了自己的联系方式，以便通过明信片回馈或者发送电子邮件的形式来让他们确认我们所募集到的善款确实用到了灾区。经过对过往同学细心的讲解，我们的做法得到了他们的认可，他们纷纷献出自己的爱心，并表示会把这个充满爱心的活动传递下去，告诉自己身边的每一个人，大家一起为灾区的孩子贡献自己的一份力量。<BR>&nbsp;&nbsp;&nbsp; 通过这次活动，我们深深感受到了身为一名党员，一名志愿者的责任感。这次活动使我们更加明白了要珍惜眼前的生活，珍惜现在拥有的一切。我们现在看似平常的生活是多少人梦寐以求的啊。希望我们这次活动能够带动更多的人来关注灾区的孩子，为孩子多送去一点欢笑；同时也希望更多的人能够感受到现有生活的来之不易，能够充实地有意义地度过每一天！<BR></P>', 680, 1273647329, '2010-05-11 22:55:29', 0, NULL, NULL, NULL),
(476, 10, '关于2010年度高考阅卷报名的通知', '', NULL, 0, '<P>报名对象：数学科学学院在读研究生</P>\r\n<P>报名方式：请将附件中表格所有内容填写清楚，于5月17日（下周一）之前发至邮箱：<A href="mailto:zhaowenli@uestc.edu.cn">zhaowenli@uestc.edu.cn</A></P>\r\n<P>注意事项：过时将不再接受报名，谢谢！</P>', 1143, 1273713958, '2010-05-12 17:25:58', 0, NULL, NULL, NULL),
(477, 11, '学院赴绵阳展开专场招生咨询会', '', NULL, 0, '<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=341" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=344" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=343" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=342" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=340" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=339" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=338" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=337" align=baseline border=0></P>\r\n<P><IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=336" align=baseline border=0><BR>&nbsp;&nbsp;&nbsp; 5月13、14两日，数学科学学院招生工作成员和招办老师前往绵阳三所重点高中进行专场的招生宣传。绵阳是四川招生的重点地方之一，这里云集了大量优秀生源，并且人数众多，是每年省内的重要生源基地。<BR>&nbsp;&nbsp;&nbsp; 学院党委副书记苟生平，招办副主任李翰超，绵阳招生工作组联络员叶谱英一行三人于5月13日上午赶到绵阳。招生工作组先后到了绵阳外语学校、南山中学和绵阳中学，并且受到几所学校校长和同学的热情接待。<BR>&nbsp;&nbsp;&nbsp; 在三个重点中学的宣讲会上，苟生平副书记非常娴熟地介绍学校的历史、特点和优势等，展示给同学们一幅美好的大学画卷，提供给大家一个成就梦想的舞台；招办李翰超副主任详细介绍学校招生的情况，分析了平行志愿下同学填报的好处和风险，给大家介绍了电子科技大学今年的招生政策，鼓励大家积极报考；叶谱英和大家一起每次都耐心接受和回答现场提问，解答考生心中的疑虑，发放招生资料，并对有意向报考电子科大的学生进行了登记，方便我们在高考完以后与他们进行联系，进行专门的咨询和解答。<BR>&nbsp;&nbsp;&nbsp; 专场宣讲会是非常成功的，从绵阳外语学校到南山中学，再到绵阳中学，招生工作组一直马不停蹄的奔波于中学之间，因为是上课期间，尤其临近高考，我们的招生工作尽量在不耽误考生学习的情况下进行。中午在绵阳外语学校，利用考生中午休息的时间进行，下午在南山中学，利用学生体育课时间进行宣讲，晚上九点半以后，招生工作组连夜赶赴绵阳中学，等待绵阳中学的考生晚自习结束。招生老师对热情的考生进行宣讲，学生激情的参与更加给招生的老师带来工作的动力，老师们耐心的讲解和回答问题，直到晚上11点多才结束。<BR>&nbsp;&nbsp;&nbsp; 这次绵阳之行不仅得到了几所中学的大力支持，巩固和保持了良好的关系，更加看到绵阳考生的热情，每场宣讲会他们都积极提问，展示了对电子科技大学的热心关注和报考的热忱，也让我们对今年四川的招生工作充满信心。</P>', 666, 1274062372, '2010-05-16 18:12:52', 0, NULL, NULL, NULL),
(478, 19, '一位人力资源总监给应届生的忠告', '', NULL, 0, '<P>&nbsp;&nbsp;&nbsp;&nbsp; 应届生，我对你“非常不满”<BR>&nbsp;&nbsp;&nbsp;&nbsp; 我很少直接去毕业生专场招人。某日广东的一个综合场，是我第一次参加的毕业生现场招聘会。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 我是以多重身份，参与这个招聘会的：作为公司的人力资源总监，我希望为公司招到一批合适的储备人才；作为一个职业规划研究者，我要深入一线接触准备投身社会就业大军——应届生的特点；作为广东省劳动学会、人力资源管理协会的理事，我想藉此了解毕业生的就业状态、以及他们的所想所思。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 到了招聘场，我感到的不是一种兴奋，而是一种压抑。这是我任职人力资源总监以来的第5年，而5年来，中国的毕业生整整翻了5倍，06年将超过360万。进场的时间还没到，售票点外已经排起了多条百米“人带”。这时，我的心中不禁掠过一丝淡淡的伤感。很多年以前，我也曾经这样在茫茫人龙中，带着满怀的期待排队，每天只能排到3、5个队伍中，去投3、5份简历，而后开始了更加漫长的心里等待……而现在，人龙的规模远远超越我作为毕业生的那个时候。现在是更多的人可以读大学了，但我无法分清，眼前的这种景象，该是用壮观来形容，还是用悲壮来形容，无法说清，这究竟是好事还是坏事。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 我做了一些统计，也思考了一些细节。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 大学生自提的薪酬水平大致如下：<BR>&nbsp;&nbsp;&nbsp;&nbsp; 研究生：全部都是说“4000元以上”。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 本科生：2000-3500元。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 大专生：基本都是提2000以下。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 其中，技术类的(如WEB开发)，相对偏高，在3000-3500之间。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 这些数据，除了研究生略有下降，其它比去年偏高(去年我是在5月份以后，才陆续通知一些人到公司面试)。一方面也许现在是刚刚开始，大家把握不到竞争线的准绳，另一方面，我希望是因为就业市场在转暖(但是看不到这种迹象)。<BR>而其中，有不少细节值得一提。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 一般情况下，我接过简历的时候，不会问也不会看应聘者是来自那个学校(我从不以学校论学生)。但是当他们回答薪酬要求的时候，却的确体现了一种差别。今天主要都是来自广东学校的学生，本科线上，暨南大学、华师大、华工、华农提的要求都比较高，要求在2500元以上；而普通一点的院校，则提2500元以下；专科线上，则有一个特殊的群体，就是所谓“非正规院校”，比如什么技工类学校、民办学校。他们提的要求，全部都是在1200元以下——问了一轮之后，我就开始对号入座的猜他们是来自那个学校，然后对照简历，我发现几乎达到“问价格则知学校”的地步。敢情是学校给了他们一个“宏观指导”？有几个技工类的大专生，都是说800元，我问他们是不是都是同学，他们说“是”。“为什么你提这个数字呢？”，他们说：“因为我们老师说，我们现在的竞争力大概就是800元”。这种对学生“批量生产，统一价格”的指导方针，令我为他们感到悲哀。老师的这一句“指导”，足以给全面摧残他们3年来的系统教育和信心，也足以毁灭这个学校——不可否认，他们中的不少人，的确市场上是按照这个价格去招聘的(特别是销售)。但是，学生不是纯粹的产品，每个人都是独一无二的，怎么可以做这种统一的指导呢？为什么不会有个别人更加有自信的提出1000、1500？我今天感受到的“好学校”和“差学校”的差别，最大和最根本的差别在于学生自信心的差别。我偶尔被邀去学校做讲座的时候，我经常会对学生说一句话：“你优秀，是因为你认为自己优秀。”学生的自我意想，自我心理定位，会永远的影响他一生。如果我是校长，我会发给这个认为自己的学生值800块的老师400块的工资，但是我不是给他4张纸币，我会换成400个1分的硬币，然后一个个往他脑袋上砸，直到他清醒过来，或者被硬币埋葬。因为他摧残了一批学生。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 今天的面试，我给每个人的时间，不会超过5分钟。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 有一个人我只和他谈了3分钟，留下他的简历，他后面的同学用一种没有希望的眼神看着他，说：“这么快？”<BR>&nbsp;&nbsp;&nbsp;&nbsp; 我说：“你会更快。如果你不假思索的用两句话描述你的优点，我就招你，1分钟”。可惜他断断续续，说了一大堆——显然他心理的“谱”，是他彩排好的自我介绍，我“打乱”了他的部署。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 有一个学生，本来我觉得不错，她只用一页的简历，就表达了她的优势和特点，当我决定招她的时候，习惯的问下学期还有没课并看简历，却看不到她的学校名字，“为什么没写学校名？”，“因为……我的学校不怎么样……”。我还是问了她的学校，然后拿起笔，在她的简历中帮她写下她的母校，然后在她眼前竖起简历：“有没写错？”<BR>&nbsp;&nbsp;&nbsp; “没有。”<BR>&nbsp;&nbsp;&nbsp;&nbsp; 我今天对所有留下的简历，只是做一个动作(在认为特别适合的，打钩)。这是我唯一写字的简历，我不知道她明不明白，我是在告诉她什么。她的做法让我大打折扣。<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果我是负责教育的，我有一把足够大的铁锤，我会抡起来把所有的中国学校都砸掉，因为现在不会有一个学校敢站出来说：我们学校是“教做人的”——全部都是“教书”的。<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 当然，也有些学生非常善于销售自我，比如，有个应聘网页设计的学生，因为他做的东西都是在学校的内部网，没法把作品展现给我看，他和我说：“我把作品放到一个叫职友集上，你可以看。”还有一个应聘文案助理的，也是将她的文章传到自己的BLOG上。晚上我回去打开他们的主页，觉得他们放在网上的内容很充实，比如应聘文案助理的学生，上面有很多她的随笔作品，我看到她不错的写作思路和思维水平，我决定明天通知这两个人上班。他们都是来自那些被人看成“差学校”的学生，但是，他们善于利用多种途径，来表达他们的优势。比如善于利用“职友集”、BLOG这种新的免费工具，的确成为他们赢得我的信任的关键。</P>\r\n<P>&nbsp;&nbsp;&nbsp;&nbsp; 总结今天的感受，我认为：<BR>&nbsp;&nbsp;&nbsp;&nbsp; 未毕业，先失业，不是因为没有工作机会提供，而是就业能力结构失衡；就业难的关键，也不是因为毕业生多，教育水平下降，而是自我认知的能力模糊，就业信心和自我价值期望的下降。天之骄子的高傲姿态消失，是好事，但取而代之的不是平衡心、平常心，而是一种自卑、浮躁和茫然。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 你可以认为我摆谱、摆资格，但是，作为毕业生，我相信你还是愿意听听一个5年人力资源总监、有过1万人次面试经历的人力资源工作者，对你的如下“不满”：<BR>&nbsp;&nbsp;&nbsp;&nbsp; 不要递给我花里胡俏的简历，给我一点简洁(不是简单)而能突出你自己的表达——面对成千上万的求职者，你真的以为HR会去看你那封100个人有99个差不多的求职信、和装订得像书本一样的简历模板内容吗？<BR>不要对我说太多的自我介绍，我不敢说3、5分钟我就准确判断了你，但是30秒之内我就定格了你的第一印象；我之所以很快结束你的面试，是因为你的后面排队的人真是太多，我也曾经作为毕业生这样苦苦的等待，我只是为了给更多的人一个和你一样平等的机会，而后我会挑选请你到我办公室复试；你认为我给你的时间太少，而流露出失望(自己是不是没被看中了？)眼神的那一刹那，你就丢失了再次获得复试的机会。我永远坚持：你的信心就是我的希望。你的岗位机会不是我给你的，而是你自己争取的。<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 不要一副“只要你招我我什么都肯做”的姿态，这样给我一种“卖身”的感觉，我请你是因为你会为公司创造价值，所以不是你“求”职，而是我“请”你。这不是叫你抬到姿态，而只是希望你抬起胸膛。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 不要对我拒绝收你的简历，报以晦气的脸色或失望的情绪。我不接收你的简历，不是你不行，而是不适合我的公司；我不接收你的简历，不是不给你机会；而是不想给你根本不存在的期待(如果我收了，你会等待着我通知你)；不是我不给你面子，而是我不想浪费你和你父母的血汗钱——尽管每一份简历只有1、2元，但是我没有任何理由，去浪费属于你的1分1厘，何况你还是一个纯消费者；我是在帮你节约，让你能够将它投给属于你的机会。我从不想当我拿不了那么多简历回公司的时候，把你的希望孤伶伶的留在招聘场地里；我更不希望，当招聘会结束以后，你的简历会在冷清的场地，像其它被丢下的简历一样，雪花般漫天飞舞，任人践踏——我决不加入这样的行列，而宁可以拒收给你打击(你也必须开始懂得直面这样的打击)，因为你熬了多少个夜晚做出来的那个简历，代表的就是你，上面写着你的价值，和你的尊严。我始终认为，任何人都可以狠狠的批判教育制度，但是绝对不可以否定你的价值，更不可以践踏你的尊严！<BR>&nbsp;&nbsp;&nbsp;&nbsp; 不要不敢说出和写下你毕业的学校，无论她是如何的差，你是从那里走出来的，因为我绝对不会要这样的人：看到他父母的时候，因为他们的背景不好而不敢认他们；或者有一天离开我的公司，去到一个更大的公司面试的时候，不好意思说我是来自一个不是500强的公司。对公司来说，你的价值和价值观，决定了一切。英雄莫问出处，如果你是一个狗熊，哪怕你从天堂走出来，也还是狗熊。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 不要给我罗列一大堆你的学习成绩和从事的所谓实习(因为很多毕业生也曾经到我这里来兜一圈，参观一下，盖个章名曰“实习”)，你只需要挑选一件特别的事情，要点式地说明过程和结果，让我知道你是怎样做事的。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 不要不敢用眼睛看着我，你不敢瞧我的时候我也瞧不起你。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 不要在回答“你的薪酬要求”的时候，多了那么多废话。我不想听每个人都重复着“因为我是毕业生，没有社会经验，所以如果公司觉得这个要求太……我也可以……”，我的钱也许也是刚刚从人民银行印出来的，但是它们不会因为还没有被流通转手，而减损它的价值。其实我不在乎你说的是5000，还是1500，我在乎的是你说出一个数字的那种语气、眼神。我尊重每个人都有自估劳动力价值的权利(但公司自有它的薪酬制度，不会特殊对待你)，但我喜欢干脆利落的同事，干脆利落是一种自信，是一种做事风格，也是一种做人态度。我会固执的认为：你说出这个数字前后带了多少个字符，就表示你做事有多拖泥带水；或者你的自我认识和自信是多么摇摆不定。<BR>&nbsp;&nbsp;&nbsp;&nbsp; 人生简短，价值无限，告诉人家你是独一无二的，你就是你，你成就你。</P>', 1414, 1274062629, '2010-05-16 18:17:09', 0, NULL, NULL, NULL),
(479, 11, '《初等数学问题的微积分思考》讲座通知', '', NULL, 0, '定于本周四下午4.20，在A313，由陆征一教授(院长）为我校学生举办《初等数学问题的微积分思考》讲座，欢迎大家参加。 ', 655, 1274153415, '2010-05-17 19:30:15', 0, NULL, NULL, NULL),
(480, 10, '创新基金项目验收事项', '', NULL, 0, '<P>请参加中期和结题验收的同学，填好附件中相应表格，于5月20日前来答辩之时带上。</P>\r\n<P>答辩具体时间：5月20日下午2:30</P>\r\n<P>地点：主楼A1区512房间</P>', 797, 1274171254, '2010-05-18 00:27:34', 0, NULL, NULL, NULL);
INSERT INTO `article` (`articleid`, `sortid`, `title`, `source`, `author`, `userid`, `content`, `click`, `publish_time`, `launch_time`, `imageid`, `keyword1`, `keyword2`, `keyword3`) VALUES
(481, 41, '关于2010年上半年上报本科毕业论文成绩及办理毕业证书工作的通知', '', NULL, 0, '<H2 class=dtitle><FONT size=3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 关于2010年上半年上报本科毕业论文成绩及办理毕业证书工作的通知</FONT></H2>\r\n<DIV class=dinfo sizset="92" sizcache="1"><SPAN class=dtime>2010年5月18日 9:55</SPAN><SPAN class=dsource>来源: 未知</SPAN><SPAN class=dhits sizset="95" sizcache="1">点击: <CODE id=dhits><FONT face=新宋体>72</FONT></CODE></SPAN></DIV>\r\n<DIV class=dcontent sizset="97" sizcache="1">\r\n<DIV style="LINE-HEIGHT: 150%; TEXT-ALIGN: right" sizset="98" sizcache="1">继教自考[2010]012号</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" align=center>&nbsp;</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" align=center sizset="104" sizcache="1">关于2010年上半年上报本科毕业论文成绩</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" align=center sizset="107" sizcache="1">及办理毕业证书工作的通知</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" align=right sizset="109" sizcache="1">&nbsp;</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" align=left sizset="111" sizcache="1">各办学单位：</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 高等教育自学考试上半年毕业证书的办理工作即将开始，由于本次办证工作时间短，为了顺利完成本次办理工作，根据四川省教育考试院川教考院自[2010]第38号文件精神，现将毕业证书办理中的有关事宜通知如下，请遵照执行。</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">一、本科毕业论文成绩上报工作</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">根据四川省教育考试院有关文件精神，本科毕业论文成绩要求全部理论课通过才能上报成绩，请各办学单位于5月18日前将学生毕业成绩上报到自考办。</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">二、毕业证书办理工作</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">1、根据省教育考试院办理毕业证书日程安排，我校工作日程安排见附件一。</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">2、资料准备工作</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">按照省教育考试院要求，凡办理高等教育自学考试毕业证书需提供以下资料：</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" sizset="120" sizcache="1">⑴毕业生登记表一份。格式见附件二；</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">⑵所有省考课的单科合格证复印件一份；</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">⑶身份证复印件一份；</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">⑷准考证复印件一份；</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">⑸须办理本科毕业证书的，学院应验其专科毕业证书原件，并在专科毕业证书复印件上加盖学院公章，经办人签名。</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">⑹各单位要将办理毕业证书的学生名单按要求格式上报，其上报格式见附件三。所上报的名单顺序分专业按准考证号由小到大顺序排列。</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">⑺凡未到校自考办照相的学生，必须上交2寸彩色照片1张（底版为深蓝色，要求为证件照）。</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">各单位将收齐的资料整理，并将所有省考课的单科合格证复印件、身份证复印件、准考证复印件和专科毕业证书复印件装订夹在毕业生登记表内。</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">3、复印格式要求</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">⑴所交的复印件必须用A4纸张复印；</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">⑵身份证和准考证复印在同一张纸上；</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">⑶毕业生登记表应用计算机输入打印，并上交纸质版和电子文档版。</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">4、根据四川省教育考试院规定，应注意以下几点：①身份证上的姓名、身份证号必须与准考证上的姓名、身份证号完全一致；②我办提供的学生考籍表上身份证号、姓名、性别必须与学生提供身份证复印件信息完全一致；③身份证号必须是18位数字。凡是15位数字的身份证号应转换为18位数字，并由当地公安部门出示户籍证明，请各办学单位严格遵照执行。</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">5、本次办理的毕业生上网注册信息将在2010年9月以后查询。</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">特此通知</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" sizset="141" sizcache="1">附件一：二○一○年上半年高等教育自学考试办理毕业证书资料收集、审查、上报日程安排表</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">附件二：四川省高等教育自学考试毕业生登记表</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" sizset="145" sizcache="1">附件三：二○一○年上半年高等教育自学考试办理毕业证书学生名单</DIV>\r\n<DIV style="LINE-HEIGHT: 150%">联系人：杨正群</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" sizset="149" sizcache="1">联系人电话号码：83202390&nbsp;&nbsp;</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" align=right sizset="152" sizcache="1">&nbsp;</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" align=right sizset="154" sizcache="1">&nbsp;</DIV>\r\n<DIV style="LINE-HEIGHT: 150%" align=right sizset="156" sizcache="1">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;二○一○年五月十四日</DIV></DIV>', 1062, 1274255827, '2010-05-18 23:57:07', 0, NULL, NULL, NULL),
(486, 11, '关于评选2009-2010年广州助学金的通知', '', NULL, 0, '各位同学：<BR>&nbsp;&nbsp;&nbsp;&nbsp; 接四川省教育基金会通知，2010年广州助学金评定工作开始。具体评定工作安排如下：<BR>1. 受助人条件：<BR>基本条件：<BR>(1) 从“三州十县二区”（即：阿坝、甘孜、凉山三州及乐山市峨边县、马边县、金口河区，绵阳市北川县、平武县，攀枝花市仁和区、米易县、盐边县，雅安市石棉县、宜宾兴文县）考入省内外高校就读的全日制大学生，不包括成人类学生、预科生。<BR>（2）2007-2009级学生，不包括2006级。<BR>（3）家庭经济困难，需提供家庭所在地（须满足第一条的“三州十县二区”）乡村、街道、社区、单位或民政部门出具的书面困难证明材料，可为复印件。<BR>（4）品行良好，学习努力、无不良嗜好，未受纪律处分。<BR>符合基本条件的下列人员优先：<BR>（1）因本人或家庭成员患重大疾病或重大意外伤害引发家庭经济特别困难的预先给予资助。<BR>（2）同等条件下，少数民族地区受灾学生以及少数民族学生优先。<BR>2.受助金额2000元/人，全校名额共7名。<BR>3. 评选及上报。<BR>&nbsp;&nbsp;&nbsp; 学生<FONT color=#ff0000>5月21日上午10点前</FONT>提出书面申请，即认真填写《“广州助学金”2009-20010年度资助金申请表》（表中“家庭经济情况和本人表现情况”栏应详细具体填写，学院签章，<FONT color=#ff0000>一式两份</FONT>）和《家庭经济困难证明材料》（可为复印件）；学院审查后推荐到学生处（每学院不超过1名），由学生处审核后上报。<BR>4.公示及发放。学生处将在审查通过学院推荐人选后在全校范围内公示。在收到资助款后发放给受助学生。 ', 1022, 1274257966, '2010-05-19 00:32:46', 0, NULL, NULL, NULL),
(487, 17, '国际青年能源与气候变化峰会', '', NULL, 0, '国际青年能源与气候变化峰会<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -----区域调研<BR>&nbsp;&nbsp; “国际青年能源与气候变化峰会”（International Youth Summit on Energy and Climate Change, YSECC） 由中国青年学生独立组织，是中国能源与气候变化领域首创性的国际性青年会议。第二届国际青年能源与气候变化峰会，以“低碳理想&#8226;青年起航”为主题，为来自中国及世界各地的青年提供一个交流的机会。<BR>&nbsp;&nbsp; 区域调研是IYSECC大会的一个重要互动环节，是面对全球青年的一次针对气候与能源的考察活动。我们组织世界各地青年团队，对自己身边跟气候与能源相关的社会，经济现象进行调研和考察，调研的内容可以是任何与大会主题相切合的项目。 <BR>你可以获得：<BR>赴上海世博会联合国会馆参加IYSECC峰会 <BR>免除本届IYSECC峰会参会费 <BR>获得2000元经费支持进行活动开展 <BR>站在世界的舞台上发表自己对气候变化见解 <BR>得到气候变化研究领域专家的亲自指点 <BR>&nbsp;<BR>参加方式<BR>&nbsp;&nbsp;&nbsp; 1．活动面向中国所有高校在校本科生和硕士研究生，已工作的青年朋友，年龄在18~28岁之间。<BR>&nbsp;&nbsp;&nbsp; 2. 在主题范围内自由提交3000~5000字调研计划，以组队的形式参加。参加团队由3-5名同学组成，鼓励多学科搭配。<BR>&nbsp;&nbsp;&nbsp; 3. 区域调研分项目申报和初评、调研、终评三个阶段。初评将选出6支队伍的调研方案进行资助，并将赴上海世博会联合国会馆IYSECC大会作现场报告。 <BR>&nbsp;<BR>&nbsp;<BR>区域调研日程安排 <BR>时间主要事宜安排<BR>5.30报名表提交截止<BR>6.7初评结果公布<BR>6.1-7.1调研活动开展<BR>6.20中期进展汇报<BR>7.1调研成果提交<BR>&nbsp;<BR>调研主题：<BR>&nbsp;&nbsp; 参赛者可选择与气候与能源相关的主题开展社会调研，调研主题包括：<BR>公民对气候变化的认知度、接受性及参与性 <BR>地方政府引领低碳能源产业发展的案例分析，包括引领低碳能源的文化氛围、完善激励低碳能源产业发展的法规政策以及政府机构的自身节能减排 <BR>企业主导节能减排的案例分析，包括企业实施技术革新，降低能耗，提高资源的利用率，实行环境友好的排放方式等 <BR>低碳经济可能带来的潜在商机（如节能家电、混合电动汽车等） <BR>&nbsp;<BR>报名方式<BR>&nbsp;&nbsp; 登陆官方网站 <A href="http://www.iysecc.org">www.iysecc.org</A> 下载报名表格<BR>&nbsp;&nbsp; 将报名表格与调研计划书一并发送到大会组委会邮箱：<A href="mailto:iysecc@gmail.com">iysecc@gmail.com</A><BR>&nbsp;&nbsp; 并请您特别注意区域调研申报截止于2010年5月30日。 <BR>联系方式<BR>联系人：郭丰<BR>手&nbsp; 机：15210892450<BR>邮&nbsp; 箱：<A href="mailto:pkuguo@163.com">pkuguo@163.com</A>&nbsp; <BR>', 1514, 1274324390, '2010-05-19 18:59:50', 0, NULL, NULL, NULL),
(483, 41, '2010年成电纵横技能培训招生简介（校本部）', '', NULL, 0, '<H2 class=dtitle>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2010年成电纵横技能培训招生简介（校本部）</H2>\r\n<DIV class=dinfo sizset="92" sizcache="1"><SPAN class=dtime>2010年4月30日 9:29</SPAN><SPAN class=dsource>来源: 未知</SPAN><SPAN class=dhits sizset="95" sizcache="1">点击: <CODE id=dhits><FONT face=新宋体>510</FONT></CODE></SPAN></DIV>\r\n<DIV class=dcontent sizset="0" sizcache="2">\r\n<H2 style="LINE-HEIGHT: 150%">招生概况</H2>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%">电子科技大学继续教育学院培训中心自开办以来，面向在校学生及社会人士，为国内IT行业及其他相关行业培训了一大批包括中、高级技术人员及管理干部在内的实用创新型人才，成为业内人士参加岗位培训、学习IT领域最新技术的职业培训基地。</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%">&nbsp;</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%">学院为了更好地履行学校服务社会的职责，充分利用完善的实验实训条件和优质的师资资源，广泛调研社会对人才的需求和企业的用人原则，总结长期从事职业教育的经验，精心制定出科学的培养目标和培训方案。结合学校校名演变的历史渊源和培训的实际内涵，倾力打造“成电纵横”技能培训品牌。 电子科技大学成人高等教育和自考助学管理规范严谨、教学质量稳定、办学特色突出，专业设置紧贴国民经济和社会发展需求，深受社会各界欢迎。</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%">&nbsp;</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%">技能培训班学员由学校协助解决食宿问题，与全日制普通本科学生一样享受学校的教育服务资源。学员可利用培训的业余时间选修高等学历教育，实现技能和学历双丰收。</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%">&nbsp;</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%" sizset="105" sizcache="1">请点击查看详情：<A style="LINE-HEIGHT: 150%" onfocus=undefined href="http://www.czj.uestc.edu.cn/news/signup/guide/2010Q/j/" target=_blank sizset="108" sizcache="1"><U style="LINE-HEIGHT: 150%">2010年成电纵横技培训招生简介</U></A></P></DIV>', 1066, 1274256222, '2010-05-19 00:03:42', 0, NULL, NULL, NULL),
(484, 41, '2010年成教网教自考招生简介（校本部）', '', NULL, 0, '<H2 class=dtitle>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2010年成教网教自考招生简介（校本部）</H2>\r\n<DIV class=dinfo sizset="92" sizcache="1"><SPAN class=dtime>2010年4月30日 8:39</SPAN><SPAN class=dsource>来源: 未知</SPAN><SPAN class=dhits sizset="95" sizcache="1">点击: <CODE id=dhits><FONT face=新宋体>1634</FONT></CODE></SPAN></DIV>\r\n<DIV class=dcontent sizset="0" sizcache="2">\r\n<H2 style="LINE-HEIGHT: 150%">招生概况</H2>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%">电子科技大学是教育部直属的全国重点大学，1997年首批成为国家“211工程”重点建设大学；2001年9月，教育部、四川省人民政府签署重点共建电子科技大学的协议，学校进入了国家《面向21世纪教育振兴行动计划》建设国内外知名高水平大学（国家“985工程”）的行列。学校现已发展成为以电子信息科学技术为核心，理、工、管、文协调发展的全国著名高等学府。</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%">电子科技大学是教育部批准的开展现代远程教育试点高校之一。学校网络教育学院以培养应用型、复合型的人才为目标，充分发挥电子科技大学学科优势，依托电子科技大学优秀的教学资源，以科学的办学态度、规范严谨的教学管理、高质量的人才培养和特色鲜明的办学模式赢得了社会广泛赞誉。 电子科技大学成人高等教育和自考助学管理规范严谨、教学质量稳定、办学特色突出，专业设置紧贴国民经济和社会发展需求，深受社会各界欢迎。</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%">2010年，我校继续面向社会举办国家承认学历（毕业文凭经国家电子注册）的高中起点本科、高中起点专科和专科起点本科的成人高等学历教育、网络高等学历教育和高教自考助学班。</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%" sizset="102" sizcache="1">请点击查看详情：<A style="LINE-HEIGHT: 150%" onfocus=undefined href="http://www.czj.uestc.edu.cn/news/signup/guide/2010Q/cwz/" target=_blank sizset="105" sizcache="1"><U style="LINE-HEIGHT: 150%">2010年成教网教自考招生简介（校本部）</U></A></P></DIV>', 1367, 1274256258, '2010-05-19 00:04:18', 0, NULL, NULL, NULL),
(485, 41, '2010电子科技大学出国留学预科项目招生', '', NULL, 0, '<H2 class=dtitle>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2010电子科技大学出国留学预科项目招生</H2>\r\n<DIV class=dinfo sizcache="1" sizset="92"><SPAN class=dtime>2010年5月6日 9:38</SPAN><SPAN class=dsource>来源: 未知</SPAN><SPAN class=dhits sizcache="1" sizset="95">点击: <CODE id=dhits><FONT face=新宋体>361</FONT></CODE></SPAN></DIV>\r\n<DIV class=dcontent sizcache="2" sizset="0">\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%">电子科技大学是教育部直属的全国重点大学、全国著名高等学府，是国家“211工程”、“985工程”重点建设高校。学校是以工为主，理工渗透，理、工、管、文协调发展的多科性大学，被江泽民同志誉为“我国电子类院校的排头兵”。</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%" sizcache="1" sizset="99">学校师资力量雄厚，现有教职工3400余人，其中专任教师2000余人，教授343人，包括中国科学院、中国工程院院士7人，国家“千人计划”入选者3人，国务院学位委员会学科组委员3人，长江学者特聘教授、讲座教授20人，国家杰出青年科技基金获得者12人，国家级教学名师奖获得者2人，全国优秀教师4人，全国师德先进个人2人，国家自然科学基金委创新群体1个，教育部创新团队2个，国防科技创新团队1个。</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%" sizcache="1" sizset="101">学校非常重视发展国际交流与合作，不断引进国外优质的教学资源、优质的品牌资源、优质的体制性软件资源。与世界30多个国家和地区的200余所大学、科研机构、企业保持着密切友好的联系。经常主办国际学术会议，在中国大学举行的国际会议次数排名中名列前茅。</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%" sizcache="1" sizset="103">学校从2005年开始开展出国留学预科项目，根据学校的办学优势和市场需求，规范办学，精细化管理，打造安全可靠的留学途径，为高中学生出国深造，打下良好语言和专业基础，做好语言、能力、专业衔接，减少留学风险，为学生提供多渠道成功之路。</P>\r\n<P style="TEXT-INDENT: 2em; LINE-HEIGHT: 150%" sizcache="1" sizset="105"><A style="LINE-HEIGHT: 150%" onfocus=undefined href="http://www.czj.uestc.edu.cn/news/signup/guide/2010/lx/" target=_blank>查看详细&gt;&gt;</A>&nbsp;</P></DIV>', 1239, 1274256490, '2010-05-19 00:08:10', 0, NULL, NULL, NULL),
(488, 11, '院“数学之星”和“杰出贡献奖”评选条件及管理办法', '', NULL, 0, '<P><BR><SPAN style="mso-hansi-font-family: 宋体"><FONT face=宋体><FONT size=3>&nbsp;&nbsp;&nbsp; 为了引导学生刻苦学习，投身实践，提高素质，全面成才，做一个优秀的大学生，我院特设立“数学之星”、“杰出贡献奖”两项个人荣誉称号。<SPAN lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></FONT></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 20pt; MARGIN: 0cm 0cm 0pt 21pt; mso-line-height-rule: exactly" class=MsoPlainText><B style="mso-bidi-font-weight: normal"><SPAN style="mso-hansi-font-family: 宋体"><FONT face=宋体><FONT size=3>一、授予条件<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></FONT></SPAN></B></P>\r\n<P style="LINE-HEIGHT: 20pt; MARGIN: 0cm 0cm 0pt; mso-line-height-rule: exactly" class=MsoPlainText><FONT size=3><SPAN style="mso-hansi-font-family: 宋体" lang=EN-US><FONT face=宋体><SPAN style="mso-spacerun: yes">&nbsp;&nbsp; </SPAN>1.</FONT></SPAN><SPAN style="FONT-FAMILY: Courier New; mso-bidi-font-family: Times New Roman; mso-ascii-font-family: 宋体" lang=EN-US>“</SPAN><FONT face=宋体>数学之星</FONT><SPAN style="FONT-FAMILY: Courier New; mso-bidi-font-family: Times New Roman; mso-ascii-font-family: 宋体" lang=EN-US>”</SPAN><FONT face=宋体>是我院最高的个人荣誉称号，其目的在于表彰学生中德、智、体诸方面表现特别优秀，为社会做出贡献，为学院赢得荣誉的学生。满足以下条件的本科应届毕业生可提出申请：</FONT></FONT></P>\r\n<P style="LINE-HEIGHT: 20pt; TEXT-INDENT: -18pt; MARGIN: 0cm 0cm 0pt 36pt; mso-line-height-rule: exactly; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt" class=MsoPlainText><SPAN style="FONT-FAMILY: Wingdings 2; mso-bidi-font-family: Wingdings 2; mso-fareast-font-family: Wingdings 2" lang=EN-US><SPAN style="mso-list: Ignore"><FONT size=3>—</FONT><SPAN style="FONT: 7pt Times New Roman">&nbsp; </SPAN></SPAN></SPAN><SPAN style="mso-hansi-font-family: 宋体"><FONT face=宋体><FONT size=3>热爱社会主义祖国，坚持四项基本原则，遵守法纪和学校规章制度，有较好的思想政治素质和道德修养<SPAN lang=EN-US> <o:p></o:p></SPAN></FONT></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 20pt; TEXT-INDENT: -18pt; MARGIN: 0cm 0cm 0pt 36pt; mso-line-height-rule: exactly; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt" class=MsoPlainText><SPAN style="FONT-FAMILY: Wingdings 2; mso-bidi-font-family: Wingdings 2; mso-fareast-font-family: Wingdings 2" lang=EN-US><SPAN style="mso-list: Ignore"><FONT size=3>—</FONT><SPAN style="FONT: 7pt Times New Roman">&nbsp; </SPAN></SPAN></SPAN><SPAN style="mso-hansi-font-family: 宋体"><FONT face=宋体><FONT size=3>两次及以上荣获人民一等奖学金及以上者可提出申请，专项奖学金获得者依据综合素质排名的入围等级来定<SPAN lang=EN-US> <o:p></o:p></SPAN></FONT></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 20pt; TEXT-INDENT: -18pt; MARGIN: 0cm 0cm 0pt 36pt; mso-line-height-rule: exactly; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt" class=MsoPlainText><SPAN style="FONT-FAMILY: Wingdings 2; mso-bidi-font-family: Wingdings 2; mso-fareast-font-family: Wingdings 2" lang=EN-US><SPAN style="mso-list: Ignore"><FONT size=3>—</FONT><SPAN style="FONT: 7pt Times New Roman">&nbsp; </SPAN></SPAN></SPAN><SPAN style="mso-hansi-font-family: 宋体"><FONT face=宋体><FONT size=3>有连续一年以上干部经历，在学习和工作中表现突出，为社会做贡献，产生良好的社会影响，并为学院赢得声誉<SPAN lang=EN-US> <o:p></o:p></SPAN></FONT></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 20pt; TEXT-INDENT: -18pt; MARGIN: 0cm 0cm 0pt 36pt; mso-line-height-rule: exactly; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt" class=MsoPlainText><SPAN style="FONT-FAMILY: Wingdings 2; mso-bidi-font-family: Wingdings 2; mso-fareast-font-family: Wingdings 2" lang=EN-US><SPAN style="mso-list: Ignore"><FONT size=3>—</FONT><SPAN style="FONT: 7pt Times New Roman">&nbsp; </SPAN></SPAN></SPAN><SPAN style="mso-hansi-font-family: 宋体"><FONT face=宋体><FONT size=3>具备较强的创新意识和实践能力，至少荣获国家级以上奖励一次，同类别奖次不累计，以最高荣誉为准<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 20pt; TEXT-INDENT: -18pt; MARGIN: 0cm 0cm 0pt 36pt; mso-line-height-rule: exactly; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt" class=MsoPlainText><SPAN style="FONT-FAMILY: Wingdings 2; mso-bidi-font-family: Wingdings 2; mso-fareast-font-family: Wingdings 2" lang=EN-US><SPAN style="mso-list: Ignore"><FONT size=3>—</FONT><SPAN style="FONT: 7pt Times New Roman">&nbsp; </SPAN></SPAN></SPAN><SPAN style="mso-hansi-font-family: 宋体"><FONT size=3><FONT face=宋体>在学科方向有科研经验，对某些学术问题有较深入的研究，至少公开发表一篇及以上论文（今年试行阶段，此条件放宽到有公开发表论文者优先）<SPAN lang=EN-US><o:p></o:p></SPAN></FONT></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 20pt; MARGIN: 0cm 0cm 0pt 18pt; mso-line-height-rule: exactly" class=MsoPlainText><FONT size=3><FONT face=宋体><SPAN style="mso-hansi-font-family: 宋体" lang=EN-US>2.</SPAN><SPAN style="mso-hansi-font-family: 宋体">“杰出贡献奖”奖励积极参与学院毕业生活动组织工作和四年来对学校、学院相关工作做出杰出贡献的同学。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></FONT></P>\r\n<P style="LINE-HEIGHT: 20pt; MARGIN: 0cm 0cm 0pt; mso-line-height-rule: exactly" class=MsoPlainText><FONT size=3><FONT face=宋体><B style="mso-bidi-font-weight: normal"><SPAN style="mso-hansi-font-family: 宋体">&nbsp;&nbsp; 二、评选和奖励办法<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></FONT></FONT></P>\r\n<P style="LINE-HEIGHT: 20pt; MARGIN: 0cm 0cm 0pt 21pt; mso-line-height-rule: exactly; mso-para-margin-left: 2.0gd" class=MsoPlainText><FONT size=3><FONT face=宋体><SPAN style="mso-hansi-font-family: 宋体" lang=EN-US>1</SPAN><SPAN style="mso-hansi-font-family: 宋体">、“数学之星”的授予名额原则上不超过应届本科毕业生人数的<SPAN lang=EN-US>1%</SPAN>，“杰出贡献奖”的授予名额原则上不超过应届本科毕业生人数的<SPAN lang=EN-US>5%</SPAN>，由本人于5月26日前提出书面申请和相关证明材料报辅导员审核，由院学生科组织审定。评选中发扬民主，认真听取学生意见并征求任课教师和其他有关同志的意见。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></FONT></P>\r\n<P style="LINE-HEIGHT: 20pt; MARGIN: 0cm 0cm 0pt 21pt; mso-line-height-rule: exactly; mso-para-margin-left: 2.0gd" class=MsoPlainText><FONT size=3><FONT face=宋体><SPAN style="mso-hansi-font-family: 宋体" lang=EN-US>2</SPAN><SPAN style="mso-hansi-font-family: 宋体">、对获得荣誉称号的个人，学院将颁发荣誉证书，载入学生档案，并给其家乡和毕业学校寄去喜报和感谢信，把他们作为我们共同学习的榜样，并使大家进一步了解电子科大，认识数学学院。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></FONT></FONT></P>\r\n<P><B><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 10.5pt; mso-hansi-font-family: Times New Roman; mso-bidi-font-family: Times New Roman; mso-ascii-font-family: Times New Roman; mso-bidi-font-size: 12.0pt; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">&nbsp;&nbsp;&nbsp; 三、《电子科技大学数学科学学院“数学之星”评选条件及管理办法》的解释权在院学生科。</SPAN></B></P>', 1236, 1274327627, '2010-05-19 19:53:47', 0, NULL, NULL, NULL),
(489, 11, '中国数学会理事陆征一给我院带来《初等数学问题的微积分思考》', '', NULL, 0, '<IMG alt="" hspace=0 src="http://www.math.uestc.edu.cn//showimg.php?iid=345" align=baseline border=0><BR>&nbsp;&nbsp;&nbsp; 5月20日下午，在清水河基础教学楼A313教室，我们请来了全国人大代表，中国数学会理事，中国数学会生物数学学会副理事长陆征一教授来我院举行《初等数学问题的微积分思考》主题讲座。党委副书记苟生平出席并讲话。<BR>&nbsp;&nbsp; 讲座伊始，陆征一教授以诙谐幽默、生动活泼的语言回顾了自己的大学生涯，拉近了与同学们的距离。陆教授说今天讲座的内容正是30年前读大学的自己对于初等数学问题的思考与疑惑，是自己大学求学历程的写照。<BR>&nbsp;&nbsp; “正弦函数的最小正周期是2π。这是一个命题还是定义？该如何求证？出处在哪？”讲座中，陆教授如数家珍地列举三角函数均值不等式，多项式重根等同学们频繁使用的结论，并对其证明、出处进行细致考证。陆教授一致强调，学习数学应该有“大胆假设，小心求证”的职业精神，而正是在这种精神的贯穿之下，陆教授为大家展示了数学不广为人知的一面。同学们也在陆教授精彩的讲解中获益良多。<BR>&nbsp;&nbsp; 讲座结束，苟生平副书记高度评价了陆教授的讲座，鼓励同学们学好数学，努力培养数学的职业感觉，为今后的学习打下坚实的基础。<BR>', 913, 1274411801, '2010-05-20 19:16:41', 0, NULL, NULL, NULL),
(490, 10, '我校学子在首届全国大学生数学竞赛决赛中喜获佳绩', '', NULL, 0, '<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center; mso-pagination: widow-orphan" align=center><B><SPAN style="FONT-SIZE: 15pt; FONT-FAMILY: 仿宋_GB2312; mso-font-kerning: 0pt; mso-ascii-font-family:  Times New Roman ">我校学子在首届全国大学生数学竞赛决赛中喜获佳绩</SPAN></B><B><SPAN lang=EN-US style="FONT-SIZE: 15pt; mso-fareast-font-family: 仿宋_GB2312; mso-font-kerning: 0pt"><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></B></P>\r\n<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: char; LINE-HEIGHT: 150%; TEXT-ALIGN: center; mso-pagination: widow-orphan; mso-outline-level: 2" align=center><FONT size=3><B><SPAN style="FONT-FAMILY: 仿宋_GB2312; mso-ascii-font-family:  Times New Roman ">作者</SPAN></B><FONT face="Times New Roman"><B><SPAN lang=EN-US style="mso-fareast-font-family: 仿宋_GB2312">:</SPAN></B><SPAN lang=EN-US style="mso-fareast-font-family: 仿宋_GB2312">&nbsp;</SPAN></FONT><SPAN style="FONT-FAMILY: 仿宋_GB2312; mso-ascii-font-family:  Times New Roman ">数学学院</SPAN><SPAN lang=EN-US style="mso-fareast-font-family: 仿宋_GB2312"><FONT face="Times New Roman"> / </FONT></SPAN><B><SPAN style="FONT-FAMILY: 仿宋_GB2312; mso-ascii-font-family:  Times New Roman ">来源</SPAN></B><B><SPAN lang=EN-US style="mso-fareast-font-family: 仿宋_GB2312"><FONT face="Times New Roman">: </FONT></SPAN></B><SPAN style="FONT-FAMILY: 仿宋_GB2312; mso-ascii-font-family:  Times New Roman ">数学学院</SPAN><SPAN lang=EN-US style="mso-fareast-font-family: 仿宋_GB2312"><o:p></o:p></SPAN></FONT></P>\r\n<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: char; LINE-HEIGHT: 150%; TEXT-ALIGN: center; mso-pagination: widow-orphan; mso-outline-level: 2" align=center><SPAN lang=EN-US style="mso-fareast-font-family: 仿宋_GB2312"><o:p><FONT face="Times New Roman" size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 150%; mso-char-indent-count: 1.5