INSERT INTO `article` (`articleid`, `sortid`, `title`, `source`, `author`, `userid`, `content`, `click`, `publish_time`, `launch_time`, `imageid`, `keyword1`, `keyword2`, `keyword3`) VALUES
(683, 9, '2011年电子科技大学教职工（含离、退休）健康体检的通知', '', NULL, 0, '<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">为方便两校区及离、退休教职工的健康体检，便于对疾病的早发现、早诊断、早治疗，<SPAN lang=EN-US>2011</SPAN>年学校教职工健康体检定于<SPAN lang=EN-US>5</SPAN>月<SPAN lang=EN-US>10</SPAN>日至<SPAN lang=EN-US>6</SPAN>月<SPAN lang=EN-US>24</SPAN>日进行，现将具体事项通知如下：（今年体检手册领取方式有重大改变，采取电话预约方式体检，请各位老师认真阅读注意事项中关于体检手册领取的方式）<SPAN lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">一、 体检时间、地点及对象：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">沙河校区体检时间：<SPAN lang=EN-US>5</SPAN>月<SPAN lang=EN-US>10</SPAN>日—<SPAN lang=EN-US>6</SPAN>月<SPAN lang=EN-US>12</SPAN>日<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">上午<SPAN lang=EN-US>8</SPAN>：<SPAN lang=EN-US>30</SPAN>—<SPAN lang=EN-US>11</SPAN>：<SPAN lang=EN-US>30<o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">下午<SPAN lang=EN-US>14</SPAN>：<SPAN lang=EN-US>30</SPAN>—<SPAN lang=EN-US>17</SPAN>：<SPAN lang=EN-US>30</SPAN>（周六、周日及节假日除外）<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">地点：沙河校区医院<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">对象：全校离、退休教职工及在职教职工<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">具体安排：<SPAN lang=EN-US>5</SPAN>月<SPAN lang=EN-US>10</SPAN>日—<SPAN lang=EN-US>5</SPAN>月<SPAN lang=EN-US>23</SPAN>日——全校离、退休教职工<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>5</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">月<SPAN lang=EN-US>24</SPAN>日—<SPAN lang=EN-US>6</SPAN>月<SPAN lang=EN-US>12</SPAN>日——全校在职教职工<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">清水河校区体检时间：<SPAN lang=EN-US>6</SPAN>月<SPAN lang=EN-US>13</SPAN>日—<SPAN lang=EN-US>6</SPAN>月<SPAN lang=EN-US>24</SPAN>日<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">上午<SPAN lang=EN-US>9</SPAN>：<SPAN lang=EN-US>00</SPAN>—<SPAN lang=EN-US>12</SPAN>：<SPAN lang=EN-US>00<o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">下午<SPAN lang=EN-US>13:00</SPAN>—<SPAN lang=EN-US>16</SPAN>：<SPAN lang=EN-US>30</SPAN>（周六、周日及节假日除外）<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">地点：清水河医院<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">对象：全校在职教职工<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">二、体检项目：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>1</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、常规检查：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>40</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">岁以下：内科、外科、耳鼻喉科、口腔科、血压、血常规、尿常规、血脂两项（总胆固醇、甘油三脂）、血糖、肝功能、乙肝两对半、肾功能、心电图、腹部<SPAN lang=EN-US>B</SPAN>超（肝胆脾胰、肾输尿管膀胱）、胸透。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>40</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">岁以上：内科、外科、耳鼻喉科、口腔科、眼底检查（离、退休人员）、血压、血常规、尿常规、血脂两项（总胆固醇甘油三脂）、血脂全套（离、退休人员）、血糖、血尿酸、肝功能、乙肝两对半、肾功能、心电图、腹部<SPAN lang=EN-US>B</SPAN>超（肝胆脾胰、肾输尿管膀胱）、胸透。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>2</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、特殊检查（自费项目）：<SPAN lang=EN-US>C</SPAN>－<SPAN lang=EN-US>12</SPAN>（肿瘤相关抗原测定）、彩超<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">三、 注意事项：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>1</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、为避免体检拥挤，保证体检质量，体检中心今年将采用电话预约体检的方式发放体检手册，每日限体检<SPAN lang=EN-US>100</SPAN>人。每日体检前一天接受第二天体检电话预约（周一体检的前一周五预约），预约请告知体检中心工作人员预约受检者的部门、姓名、工资号、联系电话，为避免体检手册遗失，体检当天请本人到体检中心告知工作人员预约的号码领取准备好的体检手册进行体检。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">预约电话：<SPAN lang=EN-US>83202406</SPAN>（沙河）<SPAN lang=EN-US> 61830397</SPAN>（清水河）<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>2</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、检查前<SPAN lang=EN-US>2</SPAN>－<SPAN lang=EN-US>3</SPAN>日应以清淡饮食为主，不吃高脂性及油炸性食物；禁食血制品及含铁量过大的食物（如猪血、鸡血、菠菜等）。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>3</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、检查前一日保证充足睡眠时间，请勿饮酒。晚餐后禁食，二十四点后禁止饮水。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>4</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、空腹检测项目（如抽血、腹部<SPAN lang=EN-US>B</SPAN>超）检查完毕后方可进食。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>5</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、做<SPAN lang=EN-US>X</SPAN>胸透检查时，勿穿带有金属钮扣衣服、项链，取下手机。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>6</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、糖尿病、高血压、心脏病、哮喘等患者，请按平时习惯服用药物。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>7</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、本着自愿的原则，愿意做特殊检查（肿瘤相关抗原测定、彩超）的体检者，费用自理（请先在体检中心领取检验单，然后在门诊收费处缴费）。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>8</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、体检者按照流程完成各项检查项目后，请将体检手则交回医院体检中心，以便为您做体检总结，提出健康咨询建议。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>9</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、体检如有异常，需复查者另行通知。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US>10</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">、在体检期间，凡是教职工家属、外聘、外校人员前来体检，将给予优惠。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">咨询电话：<SPAN lang=EN-US> 83202406</SPAN>（沙河）<SPAN lang=EN-US> 61830397</SPAN>（清水河）<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="TEXT-ALIGN: right; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=right><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt" lang=EN-US><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="TEXT-ALIGN: right; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=right><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">校医院<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-ALIGN: right; LINE-HEIGHT: 20pt; TEXT-INDENT: 28.3pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.02; mso-line-height-rule: exactly" class=MsoNormal align=right><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt">二〇一一年四月二十七日<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>', 475, 1305094954, '2011-05-10 22:22:34', 0, NULL, NULL, NULL),
(684, 11, '毕业生入伍服义务兵役政策五十问', '', NULL, 0, '关于2011年毕业生入伍服义务兵的政策解读，请毕业生认真祥读。', 569, 1305514837, '2011-05-15 19:00:37', 0, NULL, NULL, NULL),
(685, 9, '学院招生工作组赴绵阳进行招生宣传', '', NULL, 0, '<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3>近日，数学科学学院招生工作组赴绵阳进行招生的前期宣传工作。绵阳是四川招生的重点地方之一，这里云集了大量优秀生源，并且人数众多，是每年省内的重要生源基地。</FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3>工作组先后前往了绵阳中学、东辰国际学校和南山中学。除了宣讲今年的招生政策而外，也对学校今年新增加的专业进行了解读，让有意向的同学对电子科技大学有了更多的了解，鼓励大家积极报考。</FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 21.75pt; MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3>这次绵阳之行得到了中学的大力支持，进一步巩固了双方良好的关系。招生工作组看到了绵阳考生的热情和他们对电子科技大学的关注，这让大家对今年四川的招生工作充满了信心。</FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 21.75pt; MARGIN: 0cm 0cm 0pt" class=MsoNormal align=center><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><IMG border=0 hspace=0 alt="" align=baseline src="http://www.math.uestc.edu.cn//showimg.php?iid=544"></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 21.75pt; MARGIN: 0cm 0cm 0pt" class=MsoNormal align=center><SPAN style="FONT-FAMILY: 宋体; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><IMG border=0 hspace=0 alt="" align=baseline src="http://www.math.uestc.edu.cn//showimg.php?iid=543"></SPAN></P>', 707, 1305516760, '2011-05-15 19:32:40', 0, NULL, NULL, NULL),
(686, 12, '英国Heriot-Watt大学Sebastian Geiger教授学术报告', '', NULL, 0, '<P style="TEXT-ALIGN: left; TEXT-INDENT: -77pt; MARGIN: 0cm 0cm 0pt 105.05pt; mso-para-margin-left: 2.67gd; mso-char-indent-count: -5.5; mso-layout-grid-align: none" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">报告题目</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">：</SPAN><FONT face="Times New Roman"><SPAN style="COLOR: black; FONT-SIZE: 14pt; mso-bidi-font-weight: bold" lang=EN-US>Numerical methods for highresolution simulations of oil and gas production carbonate reservoirs</SPAN><SPAN style="COLOR: black; FONT-SIZE: 12pt; mso-bidi-font-weight: bold" lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></FONT></P>\r\n<P style="TEXT-ALIGN: left; TEXT-INDENT: -77pt; MARGIN: 0cm 0cm 0pt 105.05pt; mso-para-margin-left: 2.67gd; mso-char-indent-count: -5.5; mso-layout-grid-align: none" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">报</SPAN><SPAN style="FONT-SIZE: 14pt; mso-bidi-font-weight: bold; mso-fareast-font-family: 黑体"><FONT face="Times New Roman"> <SPAN style="mso-spacerun: yes">&nbsp;</SPAN><SPAN style="mso-spacerun: yes">&nbsp;</SPAN></FONT></SPAN><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">告</SPAN><SPAN style="FONT-SIZE: 14pt; mso-bidi-font-weight: bold; mso-fareast-font-family: 黑体"><FONT face="Times New Roman"> <SPAN style="mso-spacerun: yes">&nbsp;</SPAN></FONT></SPAN><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">人</SPAN><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ">：</SPAN><FONT face="Times New Roman"><SPAN style="COLOR: #0d0d0d; FONT-SIZE: 14pt; mso-ansi-language: EN-GB" lang=EN-GB>Prof. </SPAN><SPAN style="COLOR: #0d0d0d; FONT-SIZE: 14pt; mso-bidi-font-style: italic" lang=EN-US>Sebastian Geiger</SPAN><B style="mso-bidi-font-weight: normal"><I><SPAN style="COLOR: #0d0d0d; FONT-SIZE: 12pt" lang=EN-US><o:p></o:p></SPAN></I></B></FONT></P>\r\n<P style="TEXT-INDENT: 28pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold; mso-bidi-font-size: 12.0pt">报告地点</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt">：清水河校区主楼<SPAN lang=EN-US>A1-512<o:p></o:p></SPAN></SPAN></P>\r\n<P style="TEXT-INDENT: 28pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold; mso-bidi-font-size: 12.0pt">报告时间：</SPAN><SPAN style="FONT-SIZE: 14pt; mso-bidi-font-weight: bold; mso-bidi-font-size: 12.0pt; mso-fareast-font-family: 黑体"><FONT face="Times New Roman"> </FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt" lang=EN-US>5</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt">月<SPAN lang=EN-US>20</SPAN>日（周五）下午<SPAN lang=EN-US>3:30<o:p></o:p></SPAN></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><SPAN lang=EN-US><o:p><FONT size=3 face="Times New Roman">&nbsp;</FONT></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 12pt; TEXT-INDENT: 30pt; MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: char; mso-char-indent-count: 2.5; mso-layout-grid-align: none; mso-pagination: widow-orphan" class=MsoNormal><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">报告人简介：</SPAN><FONT size=3><FONT face="Times New Roman"><SPAN style="COLOR: #0d0d0d; mso-ansi-language: EN-GB" lang=EN-GB>Professor Sebastian Geiger is the CMG Industry Research Chair in Carbonate Reservoir Simulation at the Institute of Petroleum Engineering (IPE) and youngest-ever full professor at this institute. Before commencing his current role, he was a senior lecturer (2009 – 2010) and lecturer (2006 – 2009) at IPE. Sebastian holds a Dr. sc. nat. degree in Computational Geosciences from ETH Zurich (2004), a M.Sc. degree in Hydrogeology from Oregon State University (2000) and completed his undergraduate studies in geosciences at the University of Freiburg, Germany. Sebastian has been an academic visitor and visiting fellow at the Department of Earth Science and Engineering at Imperial College London (2006) and the Department of Mathematics at the Australia National University (2001), respectively. Sebastian’s current research interests lie in the simulation, modelling, understanding, and quantitative prediction of non-linear fluid flow processes in structurally complex and deformable geological media. He has published over 35 technical papers on various topics, ranging from pore-scale modelling to upscaling of multiphase flow in fractured reservoirs and high-temperature multiphase flow systems. Together with his team of nine PhD students (see </SPAN><SPAN lang=EN-US><A href="http://comphg.wikidot.com/"><SPAN style="COLOR: #0d0d0d; mso-ansi-language: EN-GB" lang=EN-GB>http://comphg.wikidot.com/</SPAN></A></SPAN><SPAN style="COLOR: #0d0d0d; mso-ansi-language: EN-GB" lang=EN-GB>), he focuses on developing new numerical, analytical, and in-situ X-Ray CT techniques to investigate pore- to the field-scale processes ubiquitous to enhanced oil and gas recovery in fractured carbonate reservoirs. Sebastian is also one of the main developers of the Complex System Modelling Platform (CSMP++), a state-of-the-art object-oriented C++ simulation environment for porous media flow, which developed jointly by the Montan University of Leoben, ETH Zurich, and Heriot-Watt University. He has served on various technical committees of the Society of Petroleum Engineers and European Association of Geoscientists and Engineers and is a council member of the International Society for Porous Media. He will co-chair the 2012 AAPG-SPE Hedberg Research Conference on “Fundamental Controls of Flow in Carbonates”.</SPAN><SPAN style="FONT-SIZE: 12pt; mso-font-kerning: 0pt; mso-ansi-language: ZH-CN"><o:p></o:p></SPAN></FONT></FONT></P>\r\n<P style="LINE-HEIGHT: 12pt; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: char; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">报告摘要：</SPAN><SPAN lang=EN-US><FONT size=3 face="Times New Roman">Carbonate reservoirs contain over 60% of the world’s remaining oil reserves (approximately 3 trillion barrel). However, these reservoirs are very difficult to produce because of their complex geological structures; hence often 80 to 90% of the available oil is left underground. The numerical simulation of oil production from subsurface hydrocarbon reservoirs is crucial to forecast optimise the management of an oil field and underpin major investment decisions such as drilling new wells or designing a new production platform. </FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 12pt; MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: char" class=MsoNormal><FONT size=3><SPAN lang=EN-US><FONT face="Times New Roman">Oil production from and multiphase (oil, gas, water) flow in subsurface hydrocarbon reservoirs can be modelled by a system of highly non</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-family: 宋体">‐</SPAN><SPAN lang=EN-US><FONT face="Times New Roman">linear partial differential equations (both of elliptic/parabolic and hyperbolic character). They need to be solved on complex geometries with coefficients varying over several orders</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-family: 宋体">‐</SPAN><SPAN lang=EN-US><FONT face="Times New Roman">of</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-family: 宋体">‐</SPAN><SPAN lang=EN-US><FONT face="Times New Roman">magnitude. In this talk I will discuss several numerical techniques that are ideally suited to simulate oil production from geometrically complex carbonate reservoirs but are not always used in commercial simulators. These include unstructured finite element – finite volume techniques, parallel algebraic multigrid solvers, TVD methods, exponential integrators, fast Newton algorithms, and stochastic particle methods.</FONT></SPAN></FONT></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 30pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.5; mso-pagination: widow-orphan" class=MsoNormal><SPAN style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt" lang=EN-US><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 15pt; MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: char; mso-layout-grid-align: none; mso-line-height-rule: exactly" class=MsoNormal align=left><SPAN style="mso-bidi-font-weight: bold; mso-bidi-font-size: 10.5pt; mso-fareast-font-family: 黑体" lang=EN-US><o:p><FONT size=3 face="Times New Roman">&nbsp;</FONT></o:p></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 110%; MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: char; mso-layout-grid-align: none" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 110%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt">欢迎全校师生参加！<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>', 723, 1305616546, '2011-05-16 23:15:46', 0, NULL, NULL, NULL),
(687, 10, '数学科学学院开设学生研讨课', '', NULL, 0, '<P style="TEXT-INDENT: 21pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><FONT size=3><?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:chsdate w:st="on" IsROCDate="False" IsLunarDate="False" Day="13" Month="5" Year="2011"><SPAN style="mso-bidi-font-size: 16.0pt" lang=EN-US><FONT face="Times New Roman">5</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">月</SPAN><SPAN style="mso-bidi-font-size: 16.0pt" lang=EN-US><FONT face="Times New Roman">13</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">日</SPAN></st1:chsdate><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">上午第</SPAN><SPAN style="mso-bidi-font-size: 16.0pt" lang=EN-US><FONT face="Times New Roman">3</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">、</SPAN><SPAN style="mso-bidi-font-size: 16.0pt" lang=EN-US><FONT face="Times New Roman">4</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">节课，清水河校区基础教学楼</SPAN><SPAN style="mso-bidi-font-size: 16.0pt" lang=EN-US><FONT face="Times New Roman">A411</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">，数学科学学院</SPAN><SPAN style="mso-bidi-font-size: 16.0pt" lang=EN-US><FONT face="Times New Roman">09</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">级全体同学举办了一堂别开生面的学生研讨课。</SPAN><SPAN style="mso-bidi-font-size: 16.0pt" lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 21pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">在</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 10.5pt">房秀芬</SPAN><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">老师的指导下，研讨课由同学自己主持。课前，同学们就结合“计算方法”课程相关知识提出了一些研究性的小问题，通过查阅资料、讨论研究形成小论文。课堂上，有六位同学介绍了自己的研究结果，并与其他同学进行了讨论。</SPAN><SPAN style="mso-bidi-font-size: 16.0pt" lang=EN-US><o:p></o:p></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 21pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><FONT size=3><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">课程组的钟尔杰老师到课旁听并做了总评。他充分肯定了同学们所做的工作，鼓励大家以后继续钻研，注重培养自己的研究能力和创新能力。</SPAN><SPAN style="mso-bidi-font-size: 16.0pt" lang=EN-US><o:p></o:p></SPAN></FONT></P>\r\n<P style="TEXT-INDENT: 21pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; mso-bidi-font-size: 16.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman "><FONT size=3>多年来，数学学院积极开展研究性教学方法改革，开设学生研讨课的课程、老师不断增加。通过学生研讨课，同学们不仅学会了如何利用图书馆的纸质资源和电子资源查阅自己需要的资料，而且学会了把查阅的资料融会贯通，并在此基础上加入自己的想法形成自己的结论，初步掌握了写研究性小论文的基本格式和方法，为以后做进一步的研究奠定了基础。</FONT></SPAN><SPAN style="mso-bidi-font-size: 16.0pt" lang=EN-US><o:p></o:p></SPAN></P>', 469, 1305772442, '2011-05-18 18:34:02', 0, NULL, NULL, NULL),
(689, 10, '2010-2011-2学期数学科学学院重修考试安排', '', NULL, 0, '请同学们相互转告。谢谢！', 904, 1305879692, '2011-05-20 00:21:32', 0, NULL, NULL, NULL),
(690, 10, '2010-2011-2学期数学科学学院重修考试安排', '', NULL, 0, '抽象代数考试日期移到5月29日8点开始，请注意查看。', 787, 1306124167, '2011-05-22 20:16:07', 0, NULL, NULL, NULL),
(691, 22, '关于评选2011届校优秀毕业研究生的通知', '', NULL, 0, '<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">各位同学：</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">根据《电子科技大学研究生奖励条例（试行）》，将在</SPAN><SPAN style="COLOR: black; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><FONT face="Times New Roman">2011</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">年毕业的研究生中进行校优秀毕业研究生的评选，现将有关事项通知如下：</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24.1pt; MARGIN: 0cm 0cm 0pt" class=MsoBodyTextIndent><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">一、评选“优秀毕业生”应具备以下基本条件：</SPAN></B><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><o:p></o:p></SPAN></B></P>\r\n<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><FONT face="Times New Roman">1</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">、热爱祖国，拥护党的领导，政治上积极要求进步，作风正派，品德优良，遵纪守法</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt"><FONT face="Times New Roman"> </FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">，愿意为国家服务，具有良好的学术道德。在校期间，各方面都起到模范带头作用。</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><FONT face="Times New Roman">2</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">、在校期间，至少获得一次以上“优秀研究生”一等奖的研究生。</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><FONT face="Times New Roman">3</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">、在校期间学业优良；学位论文必须是学位分委会评定为良好以上，具有较大创新。</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><FONT face="Times New Roman">4</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">、在科研和论文工作中做出突出贡献，被</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><FONT face="Times New Roman">SCI</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">、</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><FONT face="Times New Roman">EI</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">等收录论文多且质量高，或取得重大理论成果，或在工程项目中取得重大社会和经济效益。</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><FONT face="Times New Roman">5</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">、具有正确的择业观，对自愿献身国防事业，自愿到基层就业、创业的毕业生，在同等条件下优先推荐评选。</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><FONT face="Times New Roman">6</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">、在校期间，从未受过行政或党、团组织等任何处分。</SPAN><SPAN style="FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24.1pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">二、申请办法</SPAN></B></P>\r\n<P style="LINE-HEIGHT: 24pt; TEXT-INDENT: 24.1pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman; mso-font-kerning: 1.0pt; mso-bidi-font-family: Times New Roman; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">有意向申请的同学请把附件中登记表填好，电子档和纸质档一并于本周四（</SPAN><SPAN style="FONT-FAMILY: Times New Roman , serif; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA" lang=EN-US>5</SPAN><SPAN style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman; mso-font-kerning: 1.0pt; mso-bidi-font-family: Times New Roman; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">月</SPAN><SPAN style="FONT-FAMILY: Times New Roman , serif; COLOR: black; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA" lang=EN-US>26</SPAN><SPAN style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman; mso-font-kerning: 1.0pt; mso-bidi-font-family: Times New Roman; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">日）中午</SPAN><SPAN style="FONT-FAMILY: Times New Roman , serif; COLOR: black; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA" lang=EN-US>12</SPAN><SPAN style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 12pt; mso-bidi-font-size: 14.0pt; mso-ascii-font-family: Times 