(729, 12, '四川省科技厅2012年科技计划项目申报通知', '', NULL, 0, '<P>各位老师，您好！</P>\r\n<P><BR>&nbsp;&nbsp;&nbsp;&nbsp; 四川省科技厅2012年科技计划项目开始申报，具体通知请查看附件！</P>\r\n<P>&nbsp;&nbsp;&nbsp;&nbsp; 注意：刘燕老师将于8月16日上午10：00-12:00在电子科技大学宾馆大厅收纸质《项目申报书》两份和纸质“审查会签表”（仅要求财务处签字盖章），过时不予处理。</P>', 854, 1311662670, '2011-07-25 22:44:30', 0, NULL, NULL, NULL),
(730, 12, '香港浸会大学Michael Ng教授学术报告', '', NULL, 0, '<P style="TEXT-ALIGN: left; TEXT-INDENT: -77pt; MARGIN: 0cm 0cm 0pt 105.05pt; mso-para-margin-left: 2.67gd; mso-char-indent-count: -5.5; mso-layout-grid-align: none" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">报告题目</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">：</SPAN><SPAN style="FONT-SIZE: 14pt" lang=EN-US><FONT face="Times New Roman">Some Recent Results in Preconditioning <?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></FONT></SPAN></P>\r\n<P style="TEXT-ALIGN: left; TEXT-INDENT: -7pt; MARGIN: 0cm 0cm 0pt 105.05pt; mso-para-margin-left: 9.34gd; mso-char-indent-count: -.5; mso-layout-grid-align: none" class=MsoNormal align=left><SPAN style="FONT-SIZE: 14pt" lang=EN-US><FONT face="Times New Roman">Techniques and Tensors<o:p></o:p></FONT></SPAN></P>\r\n<P style="TEXT-ALIGN: left; TEXT-INDENT: -77pt; MARGIN: 0cm 0cm 0pt 105.05pt; mso-para-margin-left: 2.67gd; mso-char-indent-count: -5.5; mso-layout-grid-align: none" class=MsoNormal align=left><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">报</SPAN><SPAN style="FONT-SIZE: 14pt; mso-fareast-font-family: 黑体; mso-bidi-font-weight: bold"><FONT face="Times New Roman"> </FONT></SPAN><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">告</SPAN><SPAN style="FONT-SIZE: 14pt; mso-fareast-font-family: 黑体; mso-bidi-font-weight: bold"><FONT face="Times New Roman"> </FONT></SPAN><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">人</SPAN><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-ascii-font-family:  Times New Roman ">：</SPAN><SPAN style="FONT-SIZE: 14pt; mso-fareast-font-family: 黑体" lang=EN-US><FONT face="Times New Roman">Prof. Michael Ng<o:p></o:p></FONT></SPAN></P>\r\n<P style="TEXT-INDENT: 28pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">报告地点</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt">：</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt; mso-ascii-font-family:  Times New Roman ">清水河校区主楼</SPAN><SPAN style="FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt" lang=EN-US><FONT face="Times New Roman">A1-512<o:p></o:p></FONT></SPAN></P>\r\n<P style="TEXT-INDENT: 28pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">报告时间：</SPAN><SPAN style="FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt; mso-fareast-font-family: 黑体; mso-bidi-font-weight: bold" lang=EN-US><FONT face="Times New Roman">8</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt; mso-ascii-font-family:  Times New Roman ">月</SPAN><SPAN style="FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt" lang=EN-US><FONT face="Times New Roman"> 1</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt; mso-ascii-font-family:  Times New Roman ">日（周一）下午</SPAN><SPAN style="FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt" lang=EN-US><FONT face="Times New Roman">2</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt; mso-ascii-font-family:  Times New Roman ">：</SPAN><FONT face="Times New Roman"><SPAN style="FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt" lang=EN-US>00</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt" lang=EN-US><o:p></o:p></SPAN></FONT></P>\r\n<P style="LINE-HEIGHT: 18pt; TEXT-INDENT: 30pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.5; mso-line-height-rule: exactly" class=MsoNormal><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-bidi-font-weight: bold">报告人简介：</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">Michael Ng is a Professor in the Department of Mathematics at the Hong Kong Baptist University. He obtained his B.Sc. degree in 1990 and M.Phil. degree in 1992 at the <?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:PlaceType w:st="on">University</st1:PlaceType> of <st1:PlaceName w:st="on">Hong Kong</st1:PlaceName>, and Ph.D. degree in 1995 at <st1:PlaceName w:st="on">Chinese</st1:PlaceName> <st1:PlaceType w:st="on">University</st1:PlaceType> of <st1:place w:st="on">Hong Kong</st1:place>. He was a Research Fellow of Computer Sciences Laboratory at <st1:PlaceName w:st="on">Australian</st1:PlaceName> <st1:PlaceName w:st="on">National</st1:PlaceName> <st1:PlaceType w:st="on">University</st1:PlaceType> (1995-1997), and an Assistant/Associate Professor (1997-2005) of the <st1:PlaceType w:st="on">University</st1:PlaceType> of <st1:PlaceName w:st="on">Hong Kong</st1:PlaceName> before joining <st1:place w:st="on"><st1:PlaceName w:st="on">Hong Kong</st1:PlaceName> <st1:PlaceName w:st="on">Baptist</st1:PlaceName> <st1:PlaceType w:st="on">University</st1:PlaceType></st1:place>. <o:p></o:p></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 18pt; TEXT-INDENT: 30pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.5; mso-line-height-rule: exactly" class=MsoNormal><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">Michael is the Programme Director of the MSc Programme on Operational Research and Business Statistics and Director of the Centre for Mathematical Imaging and Vision (CMIV), Executive Director of the Institute for Computational Mathematics (ICM) at Hong Kong Baptist University, an Adjunct Professor of Harbin Institute of Technology, an Honorary Professor of the University of Hong Kong (2005-2011), an Adjunct Research Fellow of the E-Business Technology Institute (2000-2011), He was the Public Relations Secretary (1998-2000, 2002-2006) and the Publications Secretary (2000-2002) of the Hong Kong Mathematical Society, and the Secretary of the East Asia SIAM Section (2005-2008). He is the Council Member (since 2008) of Hong Kong Institute of Science, and the Board Member (2007-2011) of China Society for Computational Mathematics. He is the Committee Member (since 2010) of System Biology Group in China Operational Research Society. He is also the Engineering Panel Member of the Hong Kong Research Grants Council starting from 2009. His research index can be found in </FONT><A href="http://academic.research.microsoft.com/Author/308123/michael-kwok-po-ng"><SPAN style="COLOR: windowtext; TEXT-DECORATION: none; text-underline: none"><FONT face="Times New Roman">Microsoft Academic Search, </FONT></SPAN></A><FONT face="Times New Roman">and </FONT><A href="http://www.researcherid.com/rid/B-7189-2009"><SPAN style="COLOR: windowtext; TEXT-DECORATION: none; text-underline: none"><FONT face="Times New Roman">Thomson Reuters. </FONT></SPAN></A></SPAN></P>\r\n<P style="LINE-HEIGHT: 18pt; MARGIN: 0cm 0cm 0pt; mso-line-height-rule: exactly" class=MsoNormal><SPAN lang=EN-US><o:p><FONT size=3 face="Times New Roman">&nbsp;</FONT></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: 18pt; MARGIN: 0cm 0cm 0pt; mso-line-height-rule: exactly" class=MsoNormal><SPAN lang=EN-US><o:p><FONT size=3 face="Times New Roman">&nbsp;</FONT></o:p></SPAN></P>\r\n<P style="TEXT-ALIGN: left; LINE-HEIGHT: 110%; MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: char; mso-layout-grid-align: none" class=MsoNormal align=left><SPAN style="LINE-HEIGHT: 110%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt">欢迎全校师生参加！<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P>', 849, 1311753646, '2011-07-27 00:00:46', 0, NULL, NULL, NULL),
(734, 12, '数学学院教授被聘为5个国际学术期刊的主编或编委', '', NULL, 0, '<P style="TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">据悉，我校数学科学学院<?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:PersonName w:st="on" ProductID="黄廷祝">黄廷祝</st1:PersonName>教授被聘为</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">5</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">个国际学术期刊的主编或编委。</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></P>\r\n<P style="TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">被聘为主编</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">(Editor-in-Chief)</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">的为</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">Hans Publishers</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">出版的国际中文学术期刊《计算机科学与应用》。</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">从</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">2008</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">年开始，<st1:PersonName w:st="on" ProductID="黄廷祝">黄廷祝</st1:PersonName>教授被聘为</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">Hindawi Publishing Corporation</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">出版的《</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">Advances&nbsp;in&nbsp;Numerical&nbsp;Analysis</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">》和</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">Scientific Advances Publishers</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">出版的《</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">Journal&nbsp;of&nbsp;Pure&nbsp;and&nbsp;Applied&nbsp;Mathematics:&nbsp;&nbsp;Advances&nbsp;and&nbsp;Applications</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">》两个国际学术期刊编委</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; mso-char-indent-count: 2.0" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">今年被聘为</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">Scientific Research Publishing</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">出版的《</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">Advances in Pure Mathematics</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">》编委、</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><FONT face="Times New Roman">Hans Publishers</FONT></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ">出版的国际中文学术期刊《计算机科学与应用》主编和《理论数学》的编委。</SPAN><SPAN style="FONT-SIZE: 12pt" lang=EN-US><o:p></o:p></SPAN></P><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 1.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ; mso-bidi-font-family:  Times New Roman ; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">此外，</SPAN><SPAN style="FONT-FAMILY:  Times New Roman ; FONT-SIZE: 12pt; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA" lang=EN-US>2009</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 1.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ; mso-bidi-font-family:  Times New Roman ; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">年起<st1:PersonName w:st="on" ProductID="黄廷祝">黄廷祝</st1:PersonName>教授还同时被聘为美国《</SPAN><SPAN style="FONT-FAMILY:  Times New Roman ; FONT-SIZE: 12pt; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA" lang=EN-US>Mathematical Reviews</SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-font-kerning: 1.0pt; mso-ascii-font-family:  Times New Roman ; mso-hansi-font-family:  Times New Roman ; mso-bidi-font-family:  Times New Roman ; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">》评论员。</SPAN>', 1463, 1312106398, '2011-07-31 01:59:58', 0, NULL, NULL, NULL),
(753, 9, '我院与香港浸会大学数学系签署共同培养研究生协议', '', NULL, 0, '<P>&nbsp;&nbsp;&nbsp;<IMG border=0 hspace=0 alt="" align=baseline src="http://www.math.uestc.edu.cn//showimg.php?iid=617"></P>\r\n<P>&nbsp;&nbsp;&nbsp;&nbsp;<FONT size=3 face=仿宋_GB2312>8月1日，香港浸会大学数学系教授应邀访问我校数学学院，数学学院院长黄廷祝教授和香港浸会大学理学院院长黄岳顺教授共同签署了双方共同培养研究生的协议。<BR>&nbsp;&nbsp;&nbsp; 根据协议，我校数学学院与香港浸会大学数学系将在联合培养研究生、研究生互访、教授短期互访、教授课程互助等几个方面进行合作，共同培养科学研究人才。此举是我校数学学院深入推进海内外学术交流与合作，培养创新人才的又一次探索。<BR>&nbsp;&nbsp;&nbsp; 香港浸会大学数学系Michael Ng教授为师生们做了题为“Some Recent Results in Preconditioning Techniques and Tensors”的学术报告。<BR>&nbsp;&nbsp;&nbsp;<IMG border=0 hspace=0 alt="" align=baseline src="http://www.math.uestc.edu.cn//showimg.php?iid=618"> </FONT></P>\r\n<P><FONT size=3 face=仿宋_GB2312>&nbsp;&nbsp; 随后，学院研究团队分别就“图像处理高效数值算法”和“Krylov子空间算法及在电磁场计算中的应用”两个专题介绍了学校的近期研究进展。Michael Ng教授还对博士和硕士生们给予了热情指导。 </FONT></P>', 774, 1314324957, '2011-08-25 18:15:57', 0, NULL, NULL, NULL),
(759, 12, '我院9位教师获批国家自然科学基金面上项目和青年基金项目立项', '', NULL, 0, '<P style="TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: both; mso-char-indent-count: 2.0; mso-layout-grid-align: auto" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">今年我院九位教师获批国家自然科学基金项目立项，分别是：</SPAN><SPAN style="FONT-FAMILY: Times New Roman , serif; FONT-SIZE: 12pt; mso-fareast-font-family: 宋体" lang=EN-US>&nbsp;<?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></P>\r\n<P style="MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: both; mso-layout-grid-align: auto" class=MsoNormal><SPAN style="FONT-FAMILY: Times New Roman , serif; FONT-SIZE: 12pt; mso-fareast-font-family: 宋体" lang=EN-US><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: both; mso-char-indent-count: 2.0; mso-layout-grid-align: auto" class=MsoNormal><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 12pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">面上项目3</SPAN><SPAN style="FONT-FAMILY: 黑体; FONT-SIZE: 12pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">项：</SPAN><SPAN style="FONT-FAMILY: Times New Roman , serif; FONT-SIZE: 12pt; mso-fareast-font-family: 黑体" lang=EN-US><o:p></o:p></SPAN></P>\r\n<P style="TEXT-INDENT: 42pt; MARGIN: 0cm 0cm 0pt; LAYOUT-GRID-MODE: both; mso-char-indent-count: 3.5; mso-layout-grid-align: auto" class=MsoNormal><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman">黄廷祝</SPAN><SPAN style="FONT-FAMILY: Times New Roman , serif; FONT-SIZE: 12pt; mso-fareast-font-family: 宋体"> <SPAN style="mso-spacerun: yes">&nbsp;</SPAN><SPAN style="mso-spacerun: yes">&nbsp;</SPAN><SPAN style="mso-spacerun: yes">&nbsp;</SPAN><SPAN lang=EN-US>56</SPAN></SPAN><SPAN style="FONT-FAMILY: 宋体; FONT-SIZE: 12pt; mso-ascii-font-family: Tim