INSERT INTO `article` (`articleid`, `sortid`, `title`, `source`, `author`, `userid`, `content`, `click`, `publish_time`, `launch_time`, `imageid`, `keyword1`, `keyword2`, `keyword3`) VALUES
(882, 9, '应用数学学科毕业生博士论文入选2011年四川省优秀博士学位论文', '', NULL, 0, '<p style="text-indent:24pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;" class="MsoNoSpacing">\r\n	<span style="font-family:微软雅黑 , sans-serif;font-size:12pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Tahoma;">据【</span><span style="font-size:12pt;mso-hansi-font-family:Times New Roman;" lang="EN-US"><span style="font-family:Tahoma;">2012</span></span><span style="font-family:微软雅黑 , sans-serif;font-size:12pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Tahoma;">】</span><span style="font-size:12pt;mso-hansi-font-family:Times New Roman;" lang="EN-US"><span style="font-family:Tahoma;">1</span></span><span style="font-family:微软雅黑 , sans-serif;font-size:12pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Tahoma;">号文件四川省人民政府学位委员会关于批准</span><span style="font-size:12pt;mso-hansi-font-family:Times New Roman;" lang="EN-US"><span style="font-family:Tahoma;">2011</span></span><span style="font-family:微软雅黑 , sans-serif;font-size:12pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Tahoma;">年四川省优秀博士学位论文的决定，在本学院“应用数学”学科毕业的邵晋梁副教授的博士论文入选</span><span style="font-size:12pt;mso-hansi-font-family:Times New Roman;" lang="EN-US"><span style="font-family:Tahoma;">2011</span></span><span style="font-family:微软雅黑 , sans-serif;font-size:12pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Tahoma;">年四川省优秀博士学位论文。此次四川省优秀博士学位论文</span><span style="font-family:微软雅黑 , sans-serif;font-size:12pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Times New Roman;">，</span><span style="font-family:微软雅黑 , sans-serif;font-size:12pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Tahoma;">我校共有</span><span style="font-size:12pt;mso-hansi-font-family:Times New Roman;" lang="EN-US"><span style="font-family:Tahoma;">13</span></span><span style="font-family:微软雅黑 , sans-serif;font-size:12pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Times New Roman;">位毕业生</span><span style="font-family:微软雅黑 , sans-serif;font-size:12pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Tahoma;">的博士学位论文入选。</span><span style="font-size:12pt;" lang="EN-US"><!--?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /--><o:p></o:p></span>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;" class="MsoNoSpacing">\r\n	<span style="color:#666666;font-size:12pt;mso-hansi-font-family:Times New Roman;" lang="EN-US"><o:p><span style="font-family:Tahoma;"> </span></o:p></span>\r\n</p>\r\n<div align="center">\r\n	<table style="border-bottom:medium none;border-left:medium none;margin:auto auto auto -1.6pt;width:401pt;border-collapse:collapse;border-top:medium none;border-right:medium none;mso-border-alt:solid black 1.0pt;mso-yfti-tbllook:1184;mso-padding-alt:0cm 5.4pt 0cm 5.4pt;" class="MsoNormalTable" width="535" border="1" cellpadding="0" cellspacing="0">\r\n		<tbody>\r\n			<tr style="height:23.45pt;mso-yfti-irow:0;mso-yfti-firstrow:yes;">\r\n				<td style="border-bottom:black 1pt solid;border-left:black 1pt solid;padding-bottom:0cm;background-color:transparent;padding-left:5.4pt;width:283.1pt;padding-right:5.4pt;height:23.45pt;border-top:black 1pt solid;border-right:black 1pt solid;padding-top:0cm;" width="377">\r\n					<p style="text-align:center;margin:0cm 0cm 0pt;" class="MsoNoSpacing" align="center">\r\n						<span style="font-family:微软雅黑 , sans-serif;font-size:10.5pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Times New Roman;">博士学位论文题目</span><span style="font-family:宋体;font-size:10.5pt;mso-hansi-font-family:Tahoma;mso-fareast-font-family:微软雅黑;" lang="EN-US"><o:p></o:p></span>\r\n					</p>\r\n				</td>\r\n				<td style="border-bottom:black 1pt solid;border-left:#ece9d8;padding-bottom:0cm;background-color:transparent;padding-left:5.4pt;width:67.75pt;padding-right:5.4pt;height:23.45pt;border-top:black 1pt solid;border-right:black 1pt solid;padding-top:0cm;mso-border-left-alt:solid black 1.0pt;" width="90">\r\n					<p style="margin:0cm 0cm 0pt;" class="MsoNoSpacing">\r\n						<span style="font-family:微软雅黑 , sans-serif;font-size:10.5pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Times New Roman;">研究生姓名</span><span style="font-family:宋体;font-size:10.5pt;mso-hansi-font-family:Tahoma;mso-fareast-font-family:微软雅黑;" lang="EN-US"><o:p></o:p></span>\r\n					</p>\r\n				</td>\r\n				<td style="border-bottom:black 1pt solid;border-left:#ece9d8;padding-bottom:0cm;background-color:transparent;padding-left:5.4pt;width:50.15pt;padding-right:5.4pt;height:23.45pt;border-top:black 1pt solid;border-right:black 1pt solid;padding-top:0cm;mso-border-left-alt:solid black 1.0pt;" width="67">\r\n					<p style="margin:0cm 0cm 0pt;" class="MsoNoSpacing">\r\n						<span style="font-family:微软雅黑 , sans-serif;font-size:10.5pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Times New Roman;">导师</span><span style="font-family:宋体;font-size:10.5pt;mso-hansi-font-family:Tahoma;mso-fareast-font-family:微软雅黑;" lang="EN-US"><o:p></o:p></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr style="height:9.15pt;mso-yfti-irow:1;mso-yfti-lastrow:yes;">\r\n				<td style="border-bottom:black 1pt solid;border-left:black 1pt solid;padding-bottom:0cm;background-color:transparent;padding-left:5.4pt;width:283.1pt;padding-right:5.4pt;height:9.15pt;border-top:#ece9d8;border-right:black 1pt solid;padding-top:0cm;mso-border-top-alt:solid black 1.0pt;" width="377">\r\n					<p style="margin:0cm 0cm 0pt;" class="MsoNoSpacing">\r\n						<span style="font-family:微软雅黑 , sans-serif;font-size:10.5pt;mso-ascii-font-family:宋体;mso-hansi-font-family:Tahoma;mso-bidi-font-family:黑体;">矩阵分析技术在细胞神经网络稳定性分析中的应用研究</span><span style="font-family:宋体;font-size:10.5pt;mso-hansi-font-family:Tahoma;mso-fareast-font-family:微软雅黑;mso-bidi-font-family:黑体;" lang="EN-US"><o:p></o:p></span>\r\n					</p>\r\n				</td>\r\n				<td style="border-bottom:black 1pt solid;border-left:#ece9d8;padding-bottom:0cm;background-color:transparent;padding-left:5.4pt;width:67.75pt;padding-right:5.4pt;height:9.15pt;border-top:#ece9d8;border-right:black 1pt solid;padding-top:0cm;mso-border-left-alt:solid black 1.0pt;mso-border-top-alt:solid black 1.0pt;" width="90">\r\n					<p style="margin:0cm 0cm 0pt;" class="MsoNoSpacing">\r\n						<span style="font-family:微软雅黑 , sans-serif;font-size:10.5pt;mso-ascii-font-family:宋体;mso-hansi-font-family:Tahoma;">邵晋梁</span><span style="font-family:宋体;font-size:10.5pt;mso-hansi-font-family:Tahoma;mso-fareast-font-family:微软雅黑;" lang="EN-US"><o:p></o:p></span>\r\n					</p>\r\n				</td>\r\n				<td style="border-bottom:black 1pt solid;border-left:#ece9d8;padding-bottom:0cm;background-color:transparent;padding-left:5.4pt;width:50.15pt;padding-right:5.4pt;height:9.15pt;border-top:#ece9d8;border-right:black 1pt solid;padding-top:0cm;mso-border-left-alt:solid black 1.0pt;mso-border-top-alt:solid black 1.0pt;" width="67">\r\n					<p style="margin:0cm 0cm 0pt;" class="MsoNoSpacing">\r\n						<span style="font-family:微软雅黑 , sans-serif;font-size:10.5pt;mso-ascii-font-family:宋体;mso-hansi-font-family:Tahoma;">黄廷祝</span><span style="font-family:宋体;font-size:10.5pt;mso-hansi-font-family:Tahoma;mso-fareast-font-family:微软雅黑;" lang="EN-US"><o:p></o:p></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p style="margin:0cm 0cm 0pt;" class="MsoNoSpacing">\r\n	<span style="font-family:Times New Roman , serif;font-size:12pt;mso-ascii-font-family:Tahoma;" lang="EN-US"> </span><span style="font-size:12pt;mso-hansi-font-family:Times New Roman;" lang="EN-US"><span style="font-family:Tahoma;"><span style="mso-spacerun:yes;"> </span><o:p></o:p></span></span>\r\n</p>\r\n<span style="font-family:微软雅黑 , sans-serif;font-size:12pt;mso-ascii-font-family:Tahoma;mso-hansi-font-family:Tahoma;mso-bidi-font-family:Times New Roman;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;"> 在其他学院所属学科毕业的本院教师高晴副教授的博士论文、本院教师钟守铭教授作为指导教师的其他学院一毕业生博士论文本次也入选省优秀博士论文。</span>', 533, 1329899820, '2012-02-22 00:37:00', 0, NULL, NULL, NULL),
(883, 10, '2008级毕业设计进度安排表', '', NULL, 0, '<p>\r\n	<span style="font-size:x-large;"><strong><a href="/adminCotrol/editor/attached/file/20120302/20120302134006_51657.doc" target="_blank"></a>同学们，这是关系到你们一生的大事，努力吧！数学学院教务科</strong></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:x-large;"><strong>附件<a href="/adminCotrol/editor/attached/file/20120302/20120302141007_31925.doc" target="_blank">毕业设计进度安排表</a><br />\r\n</strong></span>\r\n</p>', 399, 1329974624, '2012-02-22 21:23:44', 0, NULL, NULL, NULL),
(905, 10, '关于2010级“科研实训”的通知', '', 'math_benke', 20, '<p>\r\n	<span><span style="font-size:18px;">2010级还没有参加“科研实训”的同学，请在本周内到学院看宣传栏内的导师情况介绍并在教务科周海波老师</span><span style="font-size:18px;">处报名。</span><span style="font-size:18px;">请同学们相互转告</span></span><span><strong><span style="font-size:18px;">&nbsp;。谢谢！</span><span style="font-size:18px;">&nbsp;&nbsp;&nbsp;&nbsp; </span></strong></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:32px;"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="font-size:18px;"></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="font-size:24px;">学院教务科</span></strong></span>\r\n</p>', 160, 1330929229, '2012-03-05 06:33:49', 0, NULL, NULL, NULL),
(906, 10, '《微积分II》答疑安排', '', 'math_benke', 20, '<p>\r\n	详见附件！包含清水河校区和沙河校区。请同学们相互转告，谢谢！\r\n</p>\r\n<p>\r\n	附件<a href="/adminCotrol/editor/attached/file/20120306/20120306000909_93273.xlsx" target="_blank">&lt;&lt;微积分&gt;&gt;答疑安排</a>\r\n</p>', 180, 1330936493, '2012-03-05 08:34:53', 0, NULL, NULL, NULL),
(907, 10, '2008级毕业设计初期检查通知', '', 'math_benke', 20, '<p>\r\n	<span style="font-size:24px;"><span style="font-size:32px;">2012年3月15日下午二点准时开始检查，请同学们准时到达。数学学院教务科</span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:24px;"><span style="font-size:32px;">附件：<a href="/adminCotrol/editor/attached/file/20120308/20120308165154_45527.doc" target="_blank">毕业设计初期检查</a></span></span>\r\n</p>', 199, 1331172287, '2012-03-08 02:04:47', 0, NULL, NULL, NULL),
(908, 10, '《微积分II》习题课以及答疑安排', '教务科', 'math_benke', 20, '<p>\r\n	详见附件内容！分为清水河校区和沙河校区。请同学们相互转告！\r\n</p>\r\n<p>\r\n	<a href="/adminCotrol/editor/attached/file/20120309/20120309152509_15245.xlsx" target="_blank">清水河校区习题课以及答疑安排</a>\r\n</p>\r\n<p>\r\n	<a href="/adminCotrol/editor/attached/file/20120309/20120309152536_57735.xlsx" target="_blank">沙河校区习题课以及答疑安排</a>\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>', 197, 1331277960, '2012-03-09 07:26:00', 0, NULL, NULL, NULL),
(909, 19, '东软集团2012年春季校园招聘信息', '', 'math_student', 26, '<p>\r\n	各位同学：\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 东软集团2012年春季校园招聘将于3月13日上午10点在电子科技大学清水河校区基础教学楼A104举行，宣讲完后会有相关的笔试，具体信息请查看附件。\r\n</p>\r\n<a href="/adminCotrol/editor/attached/file/20120309/20120309161847_71649.doc" target="_blank">附件</a> \r\n<p>\r\n	&nbsp;\r\n</p>', 65, 1331281001, '2012-03-09 08:16:41', 0, NULL, NULL, NULL),
(910, 9, '数学建模教练组开展教研活动', '教务科', 'math_benke', 20, '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2012年3月8日下午，数学学院数学建模教练组开展了题为“在新形势下如何更有效地开展数学建模工作”的教研活动。活动由数学建模总教练何国良副教授主持，教练组全体教师参加了活动。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 会上，教练组全体教师首先就在新形势下如何更有效地开展数学建模工作进行了热烈地讨论，提出了数学建模竞赛指导教师“老带新”的基本原则以及具体的实施办法。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 随后，曾多年担任建模总教练的徐全智教授以近几年的竞赛论文为例，对学生参加该项活动遇到的常见困难和原因作了细致而全面的分析和评述，将自己二十多年建模指导经验无私的传授给青年教师。指导教师们也就工作中遇到的问题展开了热烈和富有成效地探讨。\r\n</p>', 500, 1331627004, '2012-03-13 08:23:24', 0, NULL, NULL, NULL),
(911, 22, '数学科学学院2012年硕士研究生复试工作办法', '', 'math_keyan', 29, '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp; 根据学校关于《电子科技大学2012年硕士研究生入学考试复试工作办法》和学院的计划招生人数，经学院研究决定，学院复试分数线与学校复试分数线一致。\r\n</p>\r\n<p>\r\n	1. &nbsp;凡达到电子科技大学数学科学学院复试线的考生于2012年3月30日以前通过电话报复试笔试科目（联系电话：61831290）；\r\n</p>\r\n<p>\r\n	2. &nbsp;凡达到电子科技大学数学科学学院复试分数线的考生（含调剂生），请2012年4月5日上午9:00—17:00，凭本人身份证（原件、复印件）、复试通知单（或调剂申请单）、毕业证或学位证书、复印件（往届生）、学生证原件及复印件（应届生）、体检表到数学科学学院（清水河校区主楼A1区504研究生科）办理复试手续，复试费50元，（详情请见学校研招网电子科技大学2012年硕士研究生入学考试复试工作办法）；\r\n</p>\r\n<p>\r\n	3. &nbsp;2012年4月6日上午笔试（上午9:10—11:10 &nbsp;200分）笔试地点：A1区503； &nbsp;\r\n</p>\r\n<p>\r\n	4. &nbsp;2012年4月6日下午（1:00开始英语面试，时间5至8分钟100分、2:30开始综合面试200分）地点：A1区408、512等\r\n</p>\r\n<p>\r\n	5、复试成绩的计算方法：\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 复试成绩=笔试成绩+外语成绩+专业知识与综合面试成绩；\r\n</p>\r\n<p>\r\n	6、考生总成绩的计算方法：\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp; 总成绩=初试成绩+复试成绩\r\n</p>\r\n<p>\r\n	7、笔试成绩不低于120分，外语不低于60分，专业知识与综合面试成绩不低于120分；\r\n</p>\r\n<p>\r\n	8、录取优先顺序为：第一志愿、院内调剂、校内调剂、新生奖学金评定优先顺序与此相同。同一优先级按总成绩（初试+复试）从高到低&nbsp; 依次择优录取，新生奖学金评定与此相同。复试科目任何一项不合格直接淘汰；\r\n</p>\r\n<p>\r\n	9、院内调剂开放时间，2012年3月20---28日，校内凋剂开放时间：2012年3月20---23日，校内调剂只接收本校、本院应届的毕业生 。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 数学科学学院\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2012年3月13日\r\n</p>', 305, 1331795504, '2012-03-15 07:11:44', 0, NULL, NULL, NULL),
(914, 12, '美国威廉玛丽学院李志光教授学术报告', '', 'math_keyan', 29, '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	报告题目：Preserver problems in quantum information science\r\n</p>\r\n<p>\r\n	报 &nbsp;告 &nbsp;人：Prof. Chi-Kwong Li\r\n</p>\r\n<p>\r\n	报告地点：清水河校区主楼A1-512\r\n</p>\r\n<p>\r\n	报告时间：3月29日（周四）16：00\r\n</p>\r\n<p>\r\n	报告人简介：李志光（Chi-Kwong Li），1959年11月生于中国香港。1986年毕业于香港大学数学系，获理学博士学位。1996年被聘为威廉玛丽学院教授，2008年被聘为威廉玛丽学院弗格森教授。李志光教授是国际线性代数学会(International Linear Algebra Society) 副主席，五个国际刊物的编委。李志学院授主要研究矩阵理论，算子理论及其应用，在矩阵分析、算子不等式、矩阵代数和算子代数上线性保持问题、数值域、量子通讯中的量子纠错等领域有着很高的学术造诣和丰富的研究成果。他的研究成果在量子物理、生物信息科学、通信等领域有着广泛的应用。李志光教授取得了一批具有影响力的科研成果。在国际权威学术刊物《Journal of Functional Analysis》、《Journal of Mathematical Analysis and its Application》、《SIAM Journal on Matrix Analysis and Applications》、《Transaction of American Mathematical Society》、《Journal of Operator Theory》等数学权威刊物上发表280多篇学术论文，单篇文章的引用次数高达93次。\r\n</p>\r\n<p>\r\n	欢迎全校师生参加！\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', 448, 1332469238, '2012-03-23 02:20:38', 0, NULL, NULL, NULL),
(915, 9, '我校学子在第三届全国大学生数学竞赛决赛中喜获佳绩', '教务科', 'math_benke', 20, '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2012年3月17日，在同济大学举行了由中国数学会主办的第三届全国大学生数学竞赛决赛。进入决赛的共有来自北京大学、清华大学、浙江大学、复旦大学等高校的285名优秀大学生。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我校4名学生参加了非数学专业类的决赛，其中，张也平同学是地处四川的高校中唯一一位获全国决赛一等奖的学生。另外获2个二等奖、1个三等奖。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 本届竞赛共有来自全国500余所高校的大二及以上年级的43648名在校大学生参加预赛。\r\n</p>\r\n<p>\r\n	另，第四届全国大学生数学竞赛决赛明年上半年将在我校举行。\r\n</p>', 5475, 1333001441, '2012-03-29 06:10:41', 0, NULL, NULL, NULL);
