INSERT INTO `messageboard` (`mbid`, `mailto`, `name`, `title`, `context`, `reply`, `mtime`) VALUES
(39, 19, '李波', '复变函数的考试安排', '  我是沙河学生 重修了复变函数 可考试安排在教务系统上 没有查到啊 咨询一下具体时间', '关于考试安排请咨询教务科', 1277369091),
(42, 20, 'wds', '2009年省数学竞赛的证书', '2009年省数学竞赛的证书该到哪拿?', '清水河校区主楼A1 504教务科', 1278083050),
(47, 20, '袁璐璐', '不知报名是否录入', '老师您好，一个星期前，我在教务处报名了全国数学竞赛，可是相关老师监考微积分，不知报名情况是否录入，报名费已交，能否请老师核实一下，谢谢，电子工程学院，2802106015，身份证号650103199009183242', '你好！请打电话61831289找朱老师进行咨询。', 1278980935),
(49, 20, '陈霖', '毕业证', '我是05年来就读的电子科大,数学学院,应该09年毕业,因考试没过,补考后过了,但不知啥原因现在都没拿到毕业证,也不知在哪里拿?望告诉,谢谢.', '请把你的学号告诉我们，如是成教那边。请与成教联系。', 1279272065),
(62, 19, '肖俊', '```', '为什么 新生入学指南.pdf 我打不开呢`\r<br/>', '这是你自己的问题,在Windows下,PDF文件需要用Adobe Reader或者是Foxit reader 等软件打开.', 1281749895),
(65, 20, 'Liu ', '首届全国大学生数学竞赛（省级奖） 有获奖证书吗？', '有的话， 在什么地方领取啊？', '你好！请到清水河校区主楼A1-504教务科领取。', 1283474722),
(66, 19, '彭杰', '上课地点', '我选了数学学院的常用数学软件，杜鸿飞老师的，周3下午，但是不知道什么地点上课，我问了计算机学院的教务处，他们叫我问数学学院教务处，但是我不知道电话，能帮我查询一下上课地点么', '数学学院教务科在主楼A1区504', 1284532072),
(59, 31, '', '喜讯', '数学与应用数学被评为国家级特色专业，为什么学院网站没有及时更新？', '消息已经更新', 1280419893),
(63, 19, 'SS', '成绩问题', '为什么我数学建模重修成绩现在还没的？请问要怎么样解决 学号2804102001 麻烦查一下', '请于开学后咨询教务科61831289或是任课老师,这里是学生科,不负责成绩事宜.', 1282007109),
(58, 34, '朱文摇', '军训时间长短', '2010年清水河校区新生入校后的军训时间是多久？', '每年可能不同，约2周', 1280283948),
(95, 20, '28级', '  2010年电子科大数学竞赛的证书', '老师你好，2010年的电子科大高等数学竞赛的奖状可以领取了吗，找谁领取 ', '你好！我们会以学院为单位发到各学院教务科', 1291025113),
(93, 19, '28级', '2010年电子科大数学竞赛的证书', '老师你好，2010年的电子科大高等数学竞赛的奖状可以领取了吗，找谁领取', '请咨询我院教务科，61831289，学生科是负责本院内学生思政工作的。', 1290675961),
(196, 20, '理工学生', '关于&ldquo;双学位&rdquo;询问', '  老师您好，我是成都理工大学的大四学生，我们在大二的时候参加你你们学院与我校联办的&ldquo;工商管理&rdquo;双学位班，我想问一问，听一同学询问你们说的是给我们发的那个双学位的毕业证书和学位证书是没有在教育部电子注册的，是这样嘛，那这两个证书国家是否承认！O(&cap;_&cap;)O谢谢！', '请咨询61831290', 1300806397),
(99, 19, '个人股', '数学竞赛', '你好，两个周前数学学院老师给我打电话说上个周要给进全国大学生数学决赛的同学开个会，可我上周没得到通知，请问那会开了么？如果没开，会是什么时候开呢？', '关于数学竞赛的事宜请咨询教务科，主楼A1区504', 1291623965),
(110, 20, 'Moonshadow', '感谢人生的导师：钟尔杰', '我是22级的问题学生，跷课、挂科、降级、退学试读......数学学院的老师们看到这儿应该就知道我是谁了；多年未回母校了，今天偶进网站看到了留言版，忍不住想感谢钟尔杰教授当年对我的关爱，毫不夸张的说，要是没有您就不会有我今天的事业与幸福；当年人见都头痛的问题学生在您的关爱下己经长大，现在己是一家网游公司Android研发部门的项目负责人了；今天借母校学院留言板，在此表达一下对钟老师的感恩之情，也同时感谢数学学院老师领导对我的帮助；请不要放弃每一个差生，因为他们也将拥有属于自己的一片天空；等到有脸见钟老师那一天，希望您还记得您的问题学生，在此跪谢您的恩情；', '谢谢,我们会继续努力的!', 1294818138),
(187, 20, '27102020', '毕设', '请问校外毕设初期检查表的检查老师应该有几个人签字? 谢谢', '三人', 1299657203),
(128, 20, 'xx', '查分', '您好，请问一下我这周一去数学学院的教务科申请了查分，结果什么时候能够出来啊？如果查出了分数有问题，是否在周五前，就能在网上看到改过的分数？', '查分结果已全部出来并已上网', 1298469737),
(193, 20, '赵浩洋', '我想退一门课，但找不到老师', '我是软件学院大二的学生，这学期选了一个创新学分的课程&ldquo;随即模型与实验分析&rdquo;（王志勇老师的），后来因为和所选的其他课程发生了一些冲突，所以想退掉这个创新学分课程，但是选课已经关闭了，辅导员老师告诉我可以找到任课老师让他帮助我退掉这门课，但是信息门户所给出的课表上没有上课地点，网络学堂上又找不到这个老师的联系方式。所以希望你们能帮我退掉这门课或者给我一个王志勇老师的联系方式或者给出这个课程的上课地点。我的学号是2923104023，请及时回复，谢谢。', '同学你好！任课教师是没有权限帮你退课的，你可以联系你院教务科帮你处理此事。', 1300164160),
(194, 19, '殷秀霞', '本校外国语学院大一学生', '我想知道数学学院微积分课程的开课时间、地点。或者其他学院的微积分课也行，在网上怎么找不到？', '请联系我院教务科，61831289', 1300264502),
(195, 20, 'li', '第二届全国大学生数学竞赛（省级奖） 有获奖证书吗？', '第二届全国大学生数学竞赛（省级奖） 有获奖证书吗？有的话， 在什么地方领取啊？ ', '请到清水河校区主楼A1-504房间领取。', 1300425812),
(174, 19, '', '给&ldquo;一个大四学生&rdquo;', '你好！请联系', '“一个大四学生”：你好，你反应的情况我们已经知晓，并且很想了解真实的情况，请尽快与我们联系，谢谢。xgbjyk@uestc.edu.cn', 1299223792),
(159, 19, '仰望者', '考研', '老师您好！我是山东烟台一名二本学校的2008级学生，想考贵校的计算数学专业。听说贵校比较青睐本校的学生，我想问问一名二本学校出身的学生要考这个专业会不会有客观方面的困难呢？', '客观困难总是能通过主观努力去克服的，加油', 1298606734),
(161, 20, '王琪', '张勇老师微积分的时间地点', '老师你好,我是计科的,我想询问一下这学期张勇老师上的微积分下的上课时间和地点...非常感谢!!!', '同学你好！张勇老师这个学期未承担《微积分》课程的教学任务', 1298942493),
(473, 20, '概率论', '概率论', '老师您好 对半期考试概率论的成绩有疑问 想申请复核应该如何进行？', '向任课教师反映情况，由任课教师前来查看', 1321331349),
(470, 20, '余晓旭', '关于双学位', '老师你好，我想请问一下你们学院设有双学位吗？现在的大三学生有机会选你们学院的双学位吗？（主要担心毕业时修不够学分）', '请咨询61831290', 1320395374),
(472, 20, '求真相', '求真相', '老师您好，我是10级的，我想问下这学期数学建模选课具体是什么时候啊？只是说开课三周后，没有具体的时间，还是已经选了…？谢谢', '选课名单以考试名单为准', 1320637625),
